import { useState } from "react";
import { useCart } from "./MyCart";
import { useWishlist } from "./Wishlist";
import ProductCard from "./Home";

export default function Tshirt() {
  const [selectedColor, setSelectedColor] = useState("Black");
  const [selectedSize, setSelectedSize] = useState("M");
  const [pincode, setPincode] = useState("");
  const [deliveryMessage, setDeliveryMessage] = useState("");
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();

  const product = {
    id: 101,
    name: "Classic Cotton T-Shirt",
    price: 799,
    colors: ["Black", "White", "Blue"],
    sizes: ["S", "M", "L", "XL"],
    image: "/images/tshirt.jpg",
    description: "A premium cotton T-shirt perfect for everyday wear.",
  };

  const recommended = [
    { id: 1, name: "Denim Jacket", price: 1899, image: "/images/jacket.jpg" },
    { id: 2, name: "Casual Jeans", price: 1499, image: "/images/jeans.jpg" },
  ];

  const handlePincodeCheck = () => {
    if (!pincode) return setDeliveryMessage("Please enter a pincode");
    if (pincode.startsWith("11") || pincode.startsWith("12"))
      setDeliveryMessage("✅ Delivery available in your area!");
    else setDeliveryMessage("❌ Not deliverable to this pincode");
  };

  const handleAddToCart = () => {
    addToCart({ ...product, selectedColor, selectedSize });
    alert("Added to cart!");
  };

  const handleWishlist = () => {
    addToWishlist(product);
    alert("Added to wishlist!");
  };

  return (
    <div className="p-6 grid grid-cols-1 md:grid-cols-2 gap-8">
      {/* Left Side - Image */}
      <div>
        <img
          src={product.image}
          alt={product.name}
          className="rounded-lg w-full h-96 object-cover shadow-lg"
        />
      </div>

      {/* Right Side - Product Details */}
      <div>
        <h2 className="text-3xl font-bold">{product.name}</h2>
        <p className="text-gray-600 mt-2">{product.description}</p>
        <p className="text-2xl font-semibold mt-4">₹{product.price}</p>

        {/* Color Selection */}
        <div className="mt-4">
          <h3 className="font-semibold mb-2">Color:</h3>
          <div className="flex gap-3">
            {product.colors.map((color) => (
              <button
                key={color}
                onClick={() => setSelectedColor(color)}
                className={`px-3 py-1 rounded-lg border ${
                  selectedColor === color ? "bg-black text-white" : "bg-gray-100"
                }`}
              >
                {color}
              </button>
            ))}
          </div>
        </div>

        {/* Size Selection */}
        <div className="mt-4">
          <h3 className="font-semibold mb-2">Size:</h3>
          <div className="flex gap-3">
            {product.sizes.map((size) => (
              <button
                key={size}
                onClick={() => setSelectedSize(size)}
                className={`px-3 py-1 rounded-lg border ${
                  selectedSize === size ? "bg-black text-white" : "bg-gray-100"
                }`}
              >
                {size}
              </button>
            ))}
          </div>
        </div>

        {/* Add to Cart and Wishlist */}
        <div className="flex gap-4 mt-6">
          <button
            onClick={handleAddToCart}
            className="bg-black text-white px-6 py-2 rounded-lg hover:bg-gray-800"
          >
            Add to Cart
          </button>
          <button
            onClick={handleWishlist}
            className="border border-black px-6 py-2 rounded-lg hover:bg-gray-100"
          >
            ❤️ Wishlist
          </button>
        </div>

        {/* Pincode Check */}
        <div className="mt-6">
          <h3 className="font-semibold mb-2">Check Delivery:</h3>
          <div className="flex gap-2">
            <input
              type="text"
              value={pincode}
              onChange={(e) => setPincode(e.target.value)}
              placeholder="Enter Pincode"
              className="border p-2 rounded-lg w-40"
            />
            <button
              onClick={handlePincodeCheck}
              className="bg-black text-white px-4 py-2 rounded-lg"
            >
              Check
            </button>
          </div>
          {deliveryMessage && (
            <p className="mt-2 text-sm text-gray-700">{deliveryMessage}</p>
          )}
        </div>
      </div>

      {/* Recommended Section */}
      <div className="col-span-2 mt-12">
        <h2 className="text-2xl font-bold mb-4">You may also like</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
          {recommended.map((p) => (
            <ProductCard key={p.id} product={p} />
          ))}
        </div>
      </div>
    </div>
  );
}
