import React from "react";

const ProductCard = ({
  product_name,
  price,
  category,
  subcategory,
  details,
  product_images = [],
}) => {
  const safeProductName = product_name?.replace(/[^a-zA-Z0-9 _-]/g, "").trim();

  return (
    <div className="product-card-item">
      <a
        href={link}
        target="_blank"
        rel="noopener noreferrer"
        className="product-link"
      >
        {/* üñºÔ∏è Image Gallery */}
        <div className="product-image-gallery">
          {product_images && product_images.length > 0 ? (
            product_images.map((img, idx) => {
              // Construct local path
              const localPath = `/zara_images/${category}/${safeProductName}/${img}`;
              return (
                <img
                  key={idx}
                  src={localPath}
                  alt={`${product_name} ${idx + 1}`}
                  className="product-image"
                  loading="lazy"
                />
              );
            })
          ) : (
            <div className="no-image">No image available</div>
          )}
        </div>

        {/* üìõ Product Info */}
        <div className="product-info">
          <h3 className="product-name">{product_name}</h3>
          <p className="subcategory">{subcategory}</p>
          <p className="category">{category}</p>
          <p className="price">{price}</p>
        </div>
      </a>

      {/* üé® Inline CSS */}
      <style>{`
        .product-card-item {
          background: #fff;
          border-radius: 16px;
          box-shadow: 0 6px 20px rgba(0,0,0,0.08);
          padding: 16px;
          text-align: center;
          width: 260px;
          margin: 16px;
          display: inline-block;
          vertical-align: top;
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .product-card-item:hover {
          transform: translateY(-6px);
          box-shadow: 0 12px 24px rgba(0,0,0,0.15);
        }

        .product-link {
          text-decoration: none;
          color: inherit;
        }

        .product-image-gallery {
          display: flex;
          overflow-x: auto;
          gap: 10px;
          margin-bottom: 12px;
          scroll-behavior: smooth;
          padding-bottom: 4px;
        }

        .product-image-gallery::-webkit-scrollbar {
          height: 6px;
        }

        .product-image-gallery::-webkit-scrollbar-thumb {
          background-color: rgba(0,0,0,0.2);
          border-radius: 3px;
        }

        .product-image {
          width: 200px;
          height: 220px;
          object-fit: cover;
          border-radius: 12px;
          flex-shrink: 0;
          transition: transform 0.3s ease;
        }

        .product-image:hover {
          transform: scale(1.05);
        }

        .product-info {
          text-align: left;
          padding: 0 8px;
        }

        .product-name {
          font-size: 1rem;
          font-weight: 600;
          margin: 4px 0;
          color: #222;
          height: 42px;
          overflow: hidden;
        }

        .subcategory, .category {
          font-size: 0.8rem;
          color: #555;
          margin: 2px 0;
          text-transform: uppercase;
        }

        .price {
          font-size: 1rem;
          font-weight: 600;
          color: #000;
          margin-top: 6px;
        }

        .no-image {
          width: 100%;
          height: 220px;
          display: flex;
          align-items: center;
          justify-content: center;
          color: #999;
          font-size: 0.9rem;
          background-color: #f2f2f2;
          border-radius: 12px;
        }
      `}</style>
    </div>
  );
};

export default ProductCard;
