import React, { useState, useMemo } from "react";
import ProductCard from "./Product Card"; 
import cleaned_zara_all_data from "../Data/cleaned_zara_all_data";

const Shop = () => {
  const [sortBy, setSortBy] = useState("Featured");
  const [itemsPerPage, setItemsPerPage] = useState(16);
  const [viewType, setViewType] = useState("Grid");
  const [filter, setFilter] = useState("Subcategory");
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [showFilterPanel, setShowFilterPanel] = useState(false);

  // ---------- helper to safely parse price ----------
  const parsePrice = (p) => {
    if (typeof p === "number" && Number.isFinite(p)) return p;
    if (!p) return 0;
    try {
      const s = String(p);
      const cleaned = s.replace(/[,â‚¹\s\u00A0]/g, "");
      const onlyNumDot = cleaned.replace(/[^\d.]/g, "");
      const n = parseFloat(onlyNumDot);
      return Number.isFinite(n) ? n : 0;
    } catch {
      return 0;
    }
  };

  // ðŸ§© Apply selected filters (optional)
  const filteredData = useMemo(() => {
    let data = [...cleaned_zara_all_data];

    if (filter === "Subcategory" && selectedFilters.length > 0) {
      data = data.filter((item) => selectedFilters.includes(item.subcategory));
    } else if (filter === "Price" && selectedFilters.length > 0) {
      data = data.filter((item) => {
        const priceVal = parsePrice(item.price);
        return selectedFilters.some((f) => {
          if (f === "Below â‚¹1000") return priceVal < 1000;
          if (f === "â‚¹1000 - â‚¹3000") return priceVal >= 1000 && priceVal <= 3000;
          if (f === "Above â‚¹3000") return priceVal > 3000;
          return false;
        });
      });
    }

    return data;
  }, [filter, selectedFilters]);

  // ðŸ§© Sort products
  const sortedData = useMemo(() => {
    let sorted = [...filteredData];
    if (sortBy === "Price") sorted.sort((a, b) => parsePrice(a.price) - parsePrice(b.price));
    if (sortBy === "Name") sorted.sort((a, b) => (a.product_name || "").localeCompare(b.product_name || ""));
    return sorted;
  }, [filteredData, sortBy]);

  // ðŸ§© Paginate
  const paginatedData = sortedData.slice(0, itemsPerPage);

  return (
    <>
      <div className="top-banner">
        <div className="top-banner-content">
          <h1>Biggest Diwali Sale</h1>
          <p>This diwali get upto 80% discount on your favourite outfit.</p>
          <div className="Limited_Time">*Limited time Diwali offer</div>
          <a href="#top-products" className="shop-now-btn">
            Shop Now
          </a>
        </div>
      </div>

      <div className="product-card" id="top-products">
        <div className="product-list">
          {paginatedData.map((product) => (
            <ProductCard key={product.id} {...product} />
          ))}
        </div>
      </div>

      <style>{`
        .top-banner {
          position: relative;
          display: flex;
          justify-content: flex-start;
          align-items: center;
          padding: 40px;
          border-radius: 15px;
          margin: 40px auto;
          width: 95%;
          height: 300px;
          background-image: 
            linear-gradient(to right, rgba(245, 233, 221, 0.85), rgba(222, 220, 220, 0.7)),
            url("/Data/Images/Diwali.jpg");
          background-size: 100% 200%;
          background-position: top center;
          background-repeat: no-repeat;
          box-shadow: 0 4px 15px rgba(0,0,0,0.1);
          overflow: hidden;
        }

        .top-banner-content {
          flex: 1;
          z-index: 1;
          text-align: left;
        }

        .top-banner-content h1 {
          font-size: 2.5rem;
          color: #222;
          margin-bottom: 10px;
          font-weight: 700;
        }

        .top-banner-content p {
          font-size: 1.1rem;
          color: #333;
          margin-bottom: 15px;
        }

        .Limited_Time {
          font-size: 0.9rem;
          color: #1f0301;
          margin-bottom: 20px;
          font-weight: 600;
        }

        .shop-now-btn {
          background-color: #3b3b3b;
          color: white;
          padding: 10px 25px;
          border-radius: 25px;
          text-decoration: none;
          font-weight: bold;
          transition: all 0.3s ease;
        }

        .shop-now-btn:hover {
          background-color: #000;
          transform: scale(1.05);
        }

        .product-card h1 {
          margin-bottom: 20px;
          font-size: 2rem;
          text-align: center;
        }

        .product-list {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          gap: 25px;
        }
      `}</style>
    </>
  );
};

export default Shop;
