import React, { Component } from "react";
import zara_top_products from "../Data/zara_top_product";
import zara_popular_products from "../Data/zara_popular_product";
import ProductCard from "./Product Card"; // ‚úÖ fixed filename spacing

export default class Home extends Component {
  render() {
    const imageSet = [
      "/Data/Images/slide1.png",
      "/Data/Images/slide2.png",
      "/Data/Images/slide3.png",
    ];

    return (
      <>

        {/* üéâ Top Banner */}
        <div className="top-banner">
          <div className="top-banner-content">
            <h1>Men‚Äôs Collection</h1>
            <p>Aesthetic Men‚Äôs Wear for Every Occasion</p>
            <div className="Limited_Time">*Limited time Diwali offer</div>
            <a href="#top-products" className="shop-now-btn">
              Shop Now
            </a>
          </div>
          <img
            src="/Data/Images/BiggestDiwaliSale.png"
            alt="Men‚Äôs Fashion Banner"
            className="banner-image"
          />
        </div>

        {/* üõçÔ∏è Top Products */}
        <div className="product-card" id="top-products">
          <h1>Top Products</h1>
          <div className="product-list">
            {zara_top_products.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))}
          </div>
        </div>

        {/* üíå Newsletter */}
        <div className="Newsletter-card">
          <h1>Subscribe for latest updates on fashion</h1>
          <input type="text" placeholder="Enter your email" />
          <button>Subscribe</button>
        </div>

        {/* üî• Popular Products */}
        <div className="product-card" id="popular-products">
          <h1>Popular Products</h1>
          <div className="product-list">
            {zara_popular_products.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))}
          </div>
        </div>

        {/* üßæ Inline Styles */}
        <style>{`
          .top-banner {
            display: flex;
            justify-content: space-between;
            align-items: center;
            background: linear-gradient(to right, #fff4e6, #ffe0b3);
            padding: 40px;
            border-radius: 15px;
            margin: 40px auto;
            width: 90%;
            box-shadow: 0 4px 15px rgba(0,0,0,0.1);
          }

          .top-banner-content {
            flex: 1;
            text-align: left;
          }

          .top-banner-content h1 {
            font-size: 2.5rem;
            color: #222;
            margin-bottom: 10px;
          }

          .top-banner-content p {
            font-size: 1.1rem;
            color: #444;
            margin-bottom: 10px;
          }

          .Limited_Time {
            font-size: 0.9rem;
            color: #ff6f61;
            margin-bottom: 20px;
            font-weight: 600;
          }

          .shop-now-btn {
            background-color: #ff6f61;
            color: white;
            padding: 10px 25px;
            border-radius: 25px;
            text-decoration: none;
            font-weight: bold;
            transition: background-color 0.3s ease, transform 0.3s ease;
          }

          .shop-now-btn:hover {
            background-color: #ff4436;
            transform: scale(1.05);
          }

          .banner-image {
            width: 40%;
            max-width: 400px;
            border-radius: 15px;
            object-fit: cover;
            box-shadow: 0 4px 15px rgba(0,0,0,0.2);
          }

          .product-card {
            text-align: center;
            padding: 30px;
          }

          .product-card h1 {
            margin-bottom: 20px;
            font-size: 2rem;
          }

          .product-list {
            display: flex;
            flex-wrap: wrap;
            justify-content: center;
            gap: 20px;
          }

          .Newsletter-card {
            text-align: center;
            background: #fff0e0;
            padding: 30px;
            border-radius: 15px;
            width: 80%;
            margin: 40px auto;
          }

          .Newsletter-card input {
            padding: 10px;
            border-radius: 5px;
            border: 1px solid #ccc;
            width: 250px;
            margin-right: 10px;
          }

          .Newsletter-card button {
            background-color: #ff6f61;
            color: white;
            border: none;
            border-radius: 5px;
            padding: 10px 20px;
            font-weight: bold;
            cursor: pointer;
          }

          .Newsletter-card button:hover {
            background-color: #ff4436;
          }
        `}</style>
      </>
    );
  }
}
