import React, { useState, useEffect } from "react";
import ProductCard from "./Product Card"; // ✅ Make sure the file name matches exactly (no space)

const Shop = ({ gender }) => {
  const [products, setProducts] = useState([]);

  // ✅ Fetch data from FastAPI
  useEffect(() => {
    fetch("http://localhost:8000/products/")
      .then((res) => {
        if (!res.ok) throw new Error(`HTTP error: ${res.status}`);
        return res.json();
      })
      .then((data) => {
        console.log("✅ All products fetched:", data);
        setProducts(data);
      })
      .catch((err) => {
        console.error("❌ Error fetching products:", err);
      });
  }, []);

  // ✅ Filter products by gender (case-insensitive)
  const filteredProducts = products.filter(
    (p) => p.gender && p.gender.toLowerCase() === gender.toLowerCase()
  );

  return (
    <div className="shop-container">
      <h1 className="shop-title">{gender} Collection</h1>

      <div className="product-list">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <ProductCard key={product.id} {...product} />
          ))
        ) : (
          <p>No products found for {gender}.</p>
        )}
      </div>
    </div>
  );
};

export default Shop;
