import React, { Component } from "react";
import zara_top_products from "../Data/zara_top_product";
import zara_popular_products from "../Data/zara_popular_product";
import ProductCard from "./Product Card";
import AutoSwipeCard from "./AutoSwipeCard";

export default class Home extends Component {
  render() {
    const imageSet = [
      "/Data/Images/slide1.png",
      "/Data/Images/slide2.png",
      "/Data/Images/slide3.png",
    ];

    return (
      <>
        {/* ðŸ”¥ Auto Swipe Card Section */}
        <div style={{ textAlign: "center", marginBottom: "40px" }}>
          <h1>Explore Trending Styles</h1>
          <AutoSwipeCard images={imageSet} interval={2500} />
        </div>

        {/* ðŸª” Top Banner */}
        <div className="top-banner">
          <div className="top-banner-content">
            <h1>Biggest Diwali Sale</h1>
            <p>Get up to 80% off on your favourite Attire.</p>
            <div className="Limited_Time">*Limited time offer</div>
            <button
                className="shop-now-btn"
                onClick={() => window.location.href = '#top-products'}
              > Shop Now </button>

          </div>
          <img
            src="/Data/Images/BiggestDiwaliSale.png"
            alt="Biggest Diwali Sale"
          />
        </div>

        {/* ðŸ‘• Top Products Section */}
        <div className="product-card" id="top-products">
          <h1>Top Products</h1>
          <div className="product-list">
            {zara_top_products.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))}
          </div>
        </div>

        {/* ðŸ’Œ Newsletter Subscription */}
        <div className="Newsletter-card">
          <h1>Subscribe for latest updates on fashion.</h1>
          <input type="text" placeholder="Enter your email" />
          <button>Subscribe</button>
        </div>

        {/* ðŸŒŸ Popular Products Section */}
        <div className="product-card" id="popular-products">
          <h1>Popular Products</h1>
          <div className="product-list">
            {zara_popular_products.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))}
          </div>
        </div>
      </>
    );
  }
}
