import React, { Component } from "react";
import top_products from "../Data/top_products";

export default class Home extends Component {
  render() {
    return (
      <>
        <style>{`
          .top-banner {
            display: flex;
            justify-content: space-between;
            align-items: center;
            background: linear-gradient(90deg, #FF6F61, #FF3D00); 
            padding: 20px 40px;
            border-radius: 12px;
            color: #fff;
            width: 100%;
            box-sizing: border-box;
            margin-bottom: 20px;
          }

          .top-banner-content {
            flex: 1;
          }

          .top-banner-content h1 {
            font-size: 2rem;
            margin: 0 0 10px;
            font-weight: bold;
          }

          .top-banner-content p {
            font-size: 1.1rem;
            margin: 5px 0;
          }

          .Limited_Time {
            margin-top: 10px;
            font-size: 0.9rem;
            font-style: italic;
            text-align: left;
            color: #ffe6e0;
          }

          .top-banner img {
            width: 200px;
            height: 200px;
            object-fit: cover;
            border-radius: 12px;
            margin-left: 20px;
          }

          @media (max-width: 768px) {
            .top-banner {
              flex-direction: column;
              text-align: center;
            }

            .top-banner img {
              margin-left: 0;
              margin-top: 15px;
              width: 150px;
              height: 150px;
            }

            .Limited_Time {
              font-size: 0.7rem;
              margin-top: 8px;
              text-align: center;
            }
          }
        `}</style>

        <div className="top-banner">
          <div className="top-banner-content">
            <h1>Biggest Diwali Sale</h1>
            <p>Get up to 80% off on your favourite Attire. Limited time offer!</p>
            <p>Shop Now</p>
            <div className="Limited_Time">*Limited time offer</div>
          </div>

          <img src="/Data/Images/BiggestDiwaliSale.png" alt="Biggest Diwali Sale" />
        </div>

        <div className ="product-card">
          <h1>Top Products</h1>
          <div className="product-list">
            {top_products.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))}
            </div>


          </div>

      </>
    );
  }
}
