import productDisplay from './product';

const Item = () => {

  const { cleaned_zara_all_data } = useContext(ShopContext);
  const {id} = useParams();

  const select_product = cleaned_zara_all_data.find((p) => p.id === Number(id));

  if (!select_product) {
    return <div>Product not found</div>;
  }
