import React from "react";
import { Link } from "react-router-dom";

const products = [
  { id: 1, name: "Men’s Classic Cotton T-Shirt", price: 599, img: "/images/Men Grey Tshirt.jpg" },
  { id: 2, name: "Polo T-Shirt", price: 799, img: "/images/Women Grey Tshirt.jpg" },
  { id: 3, name: "Graphic Tee", price: 699, img: "/images/Women Orange Tshirt.jpg" },
  { id: 4, name: "Oversized Tee", price: 899, img: "/images/Women White Tshirt.jpg" },
  { id: 5, name: "Round Neck Tee", price: 499, img: "/images/Men Levendar Tshirt.jpg" },
];

const MenTopwear = () => {
  return (
    <div className="p-3">
      <h1 className="text-lg font-bold mb-3">Men’s Topwear</h1>
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-6 gap-2">
        {products.map((item) => (
          <Link
            to={`/tshirt/${item.id}`}
            key={item.id}
            className="border rounded hover:shadow-md bg-white transition-transform transform hover:-translate-y-1"
          >
            <img
              src={item.img}
              alt={item.name}
              className="w-full h-130 object-cover rounded-t"
            />
            <div className="px-2 py-1">
              <h2 className="text-[11px] font-medium leading-tight truncate">
                {item.name}
              </h2>
              <p className="text-gray-700 text-[10px] mt-0.5 font-semibold">
                ₹{item.price}
              </p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default MenTopwear;
