import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const ProductUpdate = () => {
  const navigate = useNavigate();

  const handleAddClick = () => {
    navigate("/add_product");
  };
  const [id, setId] = useState("");
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(false);
  const [updating, setUpdating] = useState(false);
  const [message, setMessage] = useState("");

  // Fetch existing product by ID
  const handleFetch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const response = await fetch(`http://localhost:8000/products/${id}`);
      if (!response.ok) throw new Error("Product not found");
      const data = await response.json();

      // Handle list or single object from backend
      const productData = Array.isArray(data) ? data[0] : data;
      setProduct(productData);
    } catch (err) {
      console.error(err);
      setMessage("‚ùå Failed to fetch product details. Please check the ID.");
      setProduct(null);
    } finally {
      setLoading(false);
    }
  };

  // Handle change for editable fields
  const handleChange = (e) => {
    setProduct({
      ...product,
      [e.target.name]: e.target.value,
    });
  };

  // Submit updated data
  const handleUpdate = async (e) => {
    e.preventDefault();
    setUpdating(true);
    setMessage("");

    try {
      const response = await fetch("http://localhost:8000/update_product/", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(product),
      });

      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      const result = await response.json();
      setMessage("‚úÖ " + result.message);
    } catch (err) {
      console.error(err);
      setMessage("‚ùå Failed to update product. Please try again.");
    } finally {
      setUpdating(false);
    }
  };

  return (
    <>
      <div className="update-container">
        <div className="card">
          <h2 className="title">üõ†Ô∏è Update Product</h2>

          {/* Fetch form */}
          <form onSubmit={handleFetch} className="fetch-form">
            <label htmlFor="id">Enter Product ID:</label>
            <input
              type="text"
              id="id"
              value={id}
              onChange={(e) => setId(e.target.value)}
              required
              placeholder="e.g. 123"
            />
            <button type="submit" disabled={loading}>
              {loading ? "Fetching..." : "Fetch Product"}
            </button>
          </form>

          {/* Display & Update Form */}
          {product && (
            <form onSubmit={handleUpdate} className="update-form">
              {Object.entries(product).map(([key, value]) => (
                <div key={key} className="form-group">
                  <label htmlFor={key}>{key}</label>
                  <input
                    type="text"
                    id={key}
                    name={key}
                    value={value}
                    onChange={handleChange}
                    disabled={key === "id"}
                  />
                </div>
              ))}

              <button type="submit" disabled={updating}>
                {updating ? "Updating..." : "Update Product"}
              </button>
              <button className="update-btn" onClick={handleAddClick}>
                Add Product
                </button>
            </form>
          )}

          {message && <div className="message">{message}</div>}
        </div>
      </div>

      <style>
        {`
          /* Layout */
          .update-container {
            display: flex;
            align-items: center;
            justify-content: center;
            min-height: 100vh;
            background: linear-gradient(135deg, #f7f9fc, #edf2f7);
            padding: 20px;
          }

          .card {
            background: #fff;
            padding: 40px;
            border-radius: 16px;
            box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
            width: 100%;
            max-width: 650px;
            transition: all 0.3s ease-in-out;
          }

          .card:hover {
            transform: translateY(-4px);
            box-shadow: 0 12px 28px rgba(0, 0, 0, 0.12);
          }

          .title {
            text-align: center;
            margin-bottom: 30px;
            font-size: 26px;
            font-weight: 600;
            color: #333;
          }

          /* Fetch form */
          .fetch-form {
            display: flex;
            gap: 10px;
            margin-bottom: 25px;
          }

          .fetch-form input {
            flex: 1;
            padding: 10px 14px;
            border: 1px solid #ccc;
            border-radius: 8px;
          }

          /* Update form */
          .update-form {
            display: flex;
            flex-direction: column;
            gap: 15px;
          }

          .form-group {
            display: flex;
            flex-direction: column;
          }

          label {
            font-size: 14px;
            font-weight: 500;
            color: #555;
            margin-bottom: 5px;
            text-transform: capitalize;
          }

          input {
            padding: 10px 14px;
            border: 1px solid #ccc;
            border-radius: 8px;
            font-size: 15px;
            transition: all 0.2s ease;
          }

          input:focus {
            outline: none;
            border-color: #4f46e5;
            box-shadow: 0 0 0 3px rgba(79, 70, 229, 0.2);
          }

          /* Buttons */
          button {
            background: #4f46e5;
            color: white;
            padding: 10px 16px;
            border: none;
            border-radius: 8px;
            font-size: 15px;
            font-weight: 600;
            cursor: pointer;
            transition: background 0.3s ease;
          }

          button:hover {
            background: #4338ca;
          }

          button:disabled {
            background: #a5a5a5;
            cursor: not-allowed;
          }

          /* Message box */
          .message {
            margin-top: 20px;
            padding: 12px;
            border-radius: 8px;
            text-align: center;
            font-weight: 500;
          }

          .message:has(.success) {
            background: #dcfce7;
            color: #166534;
          }

          .message:has(.error) {
            background: #fee2e2;
            color: #991b1b;
          }

          @media (max-width: 600px) {
            .card {
              padding: 25px;
            }
          }
        `}
      </style>
    </>
  );
};

export default ProductUpdate;
