import React, { useContext } from "react";
import { ShopContext } from "../context/Shopcontext";
import Item from "./Item";

const Shop = (props) => {
  const { allproduct } = useContext(ShopContext);

  // üéØ Banner images for each category
  const bannerImages = {
    MEN: "https://your-image-url-for-men.jpg",
    WOMAN: "https://your-image-url-for-women.jpg",
    KIDS: "https://your-image-url-for-kids.jpg",
  };

  // üíÖ Inline styles
  const styles = {
    container: {
      textAlign: "center",
      fontFamily: "Arial, sans-serif",
      backgroundColor: "#f9f9f9",
      paddingBottom: "40px",
    },
    banner: {
      width: "100%",
      height: "300px",
      backgroundImage: `url(${bannerImages[props.TYPE]})`,
      backgroundSize: "cover",
      backgroundPosition: "center",
      borderRadius: "10px",
      marginBottom: "20px",
      boxShadow: "0 4px 10px rgba(0,0,0,0.2)",
    },
    header: {
      display: "flex",
      flexWrap: "wrap",
      justifyContent: "center",
      alignItems: "center",
      gap: "10px",
      marginBottom: "20px",
      padding: "10px",
      backgroundColor: "#fff",
      borderRadius: "8px",
      boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    },
    productGrid: {
      display: "flex",
      flexWrap: "wrap",
      justifyContent: "center",
      gap: "20px",
    },
    selectBox: {
      padding: "5px",
      borderRadius: "5px",
      border: "1px solid #ccc",
    },
  };

  return (
    <div style={styles.container}>
      {/* üñºÔ∏è Banner */}
      <div style={styles.banner}></div>

      {/* üîΩ Sorting / Header Section */}
      <div style={styles.header}>
        <span>Showing 1 - 16 Products</span>
        <label>
          Sort by{" "}
          <select style={styles.selectBox}>
            <option>Featured</option>
            <option>Price</option>
            <option>Name</option>
          </select>
        </label>
        <label>
          Show{" "}
          <select style={styles.selectBox}>
            <option>16</option>
            <option>20</option>
            <option>24</option>
          </select>
        </label>
        <label>
          View{" "}
          <select style={styles.selectBox}>
            <option>Grid</option>
            <option>List</option>
          </select>
        </label>
        <span>Total Products: 54000</span>
      </div>

      {/* üõçÔ∏è Product Grid */}
      <div style={styles.productGrid}>
        {allproduct.map((item, index) => {
          if (props.TYPE === item.TYPE) {
            return (
              <Item image={item.image} category={item.category} type={item.TYPE} />
            );
          }
          return null;
        })}
      </div>
    </div>
  );
};

export default Shop;
