import React, { useContext, useState, useMemo } from "react";
import { ShopContext } from "../context/Shopcontext";
import ProductCard from "./Product Card";

const Shop = (props) => {
  const { cleaned_zara_all_data } = useContext(ShopContext);

  const [sortBy, setSortBy] = useState("Featured");
  const [itemsPerPage, setItemsPerPage] = useState(16);
  const [viewType, setViewType] = useState("Grid");
  const [filter, setFilter] = useState("Subcategory");
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [showFilterPanel, setShowFilterPanel] = useState(false);

  // ---------- helper to safely parse price ----------
  const parsePrice = (p) => {
    // if it's already a finite number, return it
    if (typeof p === "number" && Number.isFinite(p)) return p;

    // handle null/undefined/empty
    if (p === null || p === undefined || p === "") return 0;

    // coerce to string, remove non-digit except dot, then parse
    try {
      const s = String(p);
      // remove commas, rupee symbol, NBSP, spaces etc first (keeps digits and dots)
      const cleaned = s.replace(/[,₹\s\u00A0]/g, "");
      const onlyNumDot = cleaned.replace(/[^\d.]/g, "");
      const n = parseFloat(onlyNumDot);
      return Number.isFinite(n) ? n : 0;
    } catch (err) {
      console.warn("parsePrice failed for:", p, err);
      return 0;
    }
  };
  // ---------------------------------------------------

  // 🧩 Filter by category
  const filteredByCategory = cleaned_zara_all_data.filter(
    (item) => item.category === props.category
  );

  // 🧩 Unique subcategories
  const subcategories = [...new Set(filteredByCategory.map((i) => i.subcategory))];
  const prices = ["Below ₹1000", "₹1000 - ₹3000", "Above ₹3000"];

  // 🧠 Handle filter toggling
  const handleFilterChange = (value) => {
    setSelectedFilters((prev) =>
      prev.includes(value) ? prev.filter((f) => f !== value) : [...prev, value]
    );
  };

  // 🧩 Apply selected filters (uses parsePrice)
  const filteredData = useMemo(() => {
    let data = [...filteredByCategory];

    if (filter === "Subcategory" && selectedFilters.length > 0) {
      data = data.filter((item) => selectedFilters.includes(item.subcategory));
    } else if (filter === "Price" && selectedFilters.length > 0) {
      data = data.filter((item) => {
        const priceVal = parsePrice(item.price);
        return selectedFilters.some((f) => {
          if (f === "Below ₹1000") return priceVal < 1000;
          if (f === "₹1000 - ₹3000") return priceVal >= 1000 && priceVal <= 3000;
          if (f === "Above ₹3000") return priceVal > 3000;
          return false;
        });
      });
    }

    return data;
  }, [filteredByCategory, filter, selectedFilters]);

  // 🧩 Sort products (uses parsePrice)
  const sortedData = useMemo(() => {
    let sorted = [...filteredData];
    if (sortBy === "Price")
      sorted.sort((a, b) => parsePrice(a.price) - parsePrice(b.price));
    if (sortBy === "Name")
      sorted.sort((a, b) => (a.product_name || "").localeCompare(b.product_name || ""));
    return sorted;
  }, [filteredData, sortBy]);

  // 🧩 Paginate data
  const paginatedData = sortedData.slice(0, itemsPerPage);

  // 🖼️ Build correct local image paths (unchanged, handles CDN URLs too)
  const dataWithLocalImages = useMemo(() => {
    return paginatedData.map((item) => {
      let images = [];

      if (item.product_images) {
        try {
          let parsed = item.product_images;
          if (typeof parsed === "string") parsed = JSON.parse(parsed.replace(/'/g, '"'));

          images = parsed.map((img) => {
            const url = typeof img === "string" ? img : Object.keys(img)[0];

            // If it's already a full URL, return it
            if (typeof url === "string" && url.startsWith("http")) return url;

            // else assume local filename in public/zara_images/{category}/{subcategory}/
            const category = item.category?.trim() || "Men";
            const subcategory = item.subcategory
              ?.trim()
              .replace(/\s+/g, "_")
              .replace(/[^\w-]/g, "");

            return `${process.env.PUBLIC_URL}/zara_images/${category}/${subcategory}/${url}`;
          });
        } catch (err) {
          console.error("Error parsing product_images for", item.product_name, err);
        }
      }

      return { ...item, product_images: images };
    });
  }, [paginatedData]);

  return (
    <div className="shop-container">
      {/* ... your styles and markup (unchanged) ... */}

      <style>{/* your styles here (omitted for brevity) */}</style>

      {/* banner + filters + product grid — use dataWithLocalImages */}
      <div className="banner-section">{/* ... */}</div>

      <div className="shop-content">
        {showFilterPanel && (
          <div className="filter-sidebar">
            <h3>{filter} Filter</h3>
            {(filter === "Subcategory" ? subcategories : prices).map((value) => (
              <div key={value} className="filter-option">
                <label>
                  <input
                    type="checkbox"
                    checked={selectedFilters.includes(value)}
                    onChange={() => handleFilterChange(value)}
                  />
                  {value}
                </label>
              </div>
            ))}
            <button className="apply-btn" onClick={() => setShowFilterPanel(false)}>
              Apply Filter
            </button>
          </div>
        )}

        <div className="product-area">
          {/* header controls */}
          <div className="shop-header">
            <span>
              Showing 1 - {Math.min(itemsPerPage, sortedData.length)} of {sortedData.length} Products
            </span>
            {/* rest of controls ... */}
          </div>

          <div className={viewType === "Grid" ? "product-grid" : "product-list"}>
            {dataWithLocalImages.map((item) => (
              <div className="product-card-item" key={item.id || item.product_name}>
                <ProductCard {...item} />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Shop;
