import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { useCart } from "../../context/CartContext";
import ProductCard from "./Product Card";

const ProductDisplay = () => {
  const { id } = useParams();
  const { addToCart } = useCart();

  const [product, setProduct] = useState(null);
  const [popularProducts, setPopularProducts] = useState([]);
  const [activeImage, setActiveImage] = useState(0);
  const [selectedSize, setSelectedSize] = useState("M");
  const [error, setError] = useState(null);

  /* ================= FETCH PRODUCT ================= */
  useEffect(() => {
    fetch(`http://localhost:8000/products/${id}/`)
      .then((res) => res.json())
      .then(setProduct)
      .catch(() => setError("Product Error"));
  }, [id]);

  /* ================= FETCH POPULAR PRODUCTS ================= */
  useEffect(() => {
    fetch("http://localhost:8000/popular_products/")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setPopularProducts(data);
        else if (Array.isArray(data.popular_products))
          setPopularProducts(data.popular_products);
        else setPopularProducts([]);
      })
      .catch(() => setPopularProducts([]));
  }, []);

  if (error) return <h3>{error}</h3>;
  if (!product) return <h3>Loading...</h3>;

  /* ================= ADD TO CART ================= */
  const handleAddToCart = () => {
    addToCart({
      id: product.id,
      name: product.product_name,
      price: product.price,
      image: product.images[0],
      size: selectedSize,
      quantity: 1,
    });
  };

  return (
    <>
      {/* ================= PRODUCT SECTION ================= */}
      <div className="product-wrapper">

        {/* LEFT — IMAGE CARD */}
        <div className="image-card">
          <div className="image-container">
            <img
              src={product.images[activeImage]}
              alt={product.product_name}
              className="product-image"
            />
          </div>

          <div className="thumbnail-row">
            {product.images.map((img, i) => (
              <img
                key={i}
                src={img}
                className={`thumbnail ${activeImage === i ? "active" : ""}`}
                onClick={() => setActiveImage(i)}
                alt=""
              />
            ))}
          </div>
        </div>

        {/* RIGHT — DETAILS */}
        <div className="product-details">
          <h2 className="product-name">{product.product_name}</h2>
          <p className="gender">{product.gender}</p>
          <p className="price">₹{product.price}</p>

          {/* SIZE */}
          <div className="size-selector">
            {["S", "M", "L", "XL"].map((size) => (
              <button
                key={size}
                className={`size ${selectedSize === size ? "active" : ""}`}
                onClick={() => setSelectedSize(size)}
              >
                {size}
              </button>
            ))}
          </div>

          {/* ACTIONS */}
          <Link to="/MyCart">
            <button className="btn primary" onClick={handleAddToCart}>
              Add to Cart
            </button>
          </Link>

          <button className="btn secondary">Buy Now</button>

          <div className="action-row">
            <button className="btn outline">❤️ Wishlist</button>
            <button className="btn outline">
              <svg width="18" height="18" viewBox="0 0 24 24">
                <path
                  d="M12 3v12m0-12l4 4m-4-4l-4 4M5 15v4a2 2 0 002 2h10a2 2 0 002-2v-4"
                  stroke="black"
                  strokeWidth="2"
                  fill="none"
                />
                Share
              </svg>
            </button>
          </div>

          {/* DESCRIPTION */}
          <div className="description">
            <h4>Description</h4>
            <p>{product.details}</p>
          </div>
        </div>
      </div>

      {/* ================= POPULAR PRODUCTS ================= */}
      <div className="popular-section">
        <h2 className="popular-title">Popular Products</h2>

        <div className="product-list">
          {popularProducts.map((item) => (
            <ProductCard key={item.id} {...item} />
          ))}
        </div>
      </div>

      {/* ================= STYLES ================= */}
      <style>{`
        .product-wrapper {
          display: flex;
          gap: 40px;
          max-width: 1100px;
          margin: 40px auto;
          padding: 20px;
        }

        .image-card {
          width: 420px;
          padding: 16px;
          border-radius: 18px;
          background: #fff;
          box-shadow: 0 12px 30px rgba(0,0,0,0.12);
        }

        .image-container {
          height: 460px;
          overflow: hidden;
          border-radius: 14px;
        }

        .product-image {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .thumbnail-row {
          display: flex;
          gap: 10px;
          margin-top: 14px;
          justify-content: center;
        }

        .thumbnail {
          width: 60px;
          height: 60px;
          border-radius: 8px;
          opacity: 0.6;
          cursor: pointer;
        }

        .thumbnail.active {
          opacity: 1;
          outline: 2px solid #000;
        }

        .product-details {
          flex: 1;
        }

        .product-name {
          font-size: 28px;
        }

        .gender {
          color: #777;
          margin-bottom: 10px;
        }

        .price {
          font-size: 22px;
          font-weight: 600;
          margin-bottom: 20px;
        }

        .size-selector {
          display: flex;
          gap: 12px;
          margin-bottom: 24px;
        }

        .size {
          padding: 10px 16px;
          border-radius: 24px;
          border: 1px solid #ccc;
          cursor: pointer;
        }

        .size.active {
          background: #000;
          color: #fff;
        }

        .btn {
          width: 100%;
          padding: 14px;
          border-radius: 12px;
          margin-bottom: 12px;
          cursor: pointer;
        }

        .primary {
          background: #000;
          color: #fff;
        }

        .secondary {
          background: #ff9800;
          color: #fff;
        }

        .outline {
          border: 1px solid #ccc;
          background: #fff;
          flex: 1;
        }

        .action-row {
          display: flex;
          gap: 12px;
        }

        .description {
          margin-top: 20px;
          border-top: 1px solid #eee;
          padding-top: 16px;
        }

        .popular-section {
          max-width: 1200px;
          margin: 80px auto;
          padding: 0 20px;
        }

        .popular-title {
          text-align: center;
          font-size: 28px;
          margin-bottom: 30px;
        }

        .product-list {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
          gap: 30px;
        }

        @media (max-width: 768px) {
          .product-wrapper {
            flex-direction: column;
            align-items: center;
          }
          .image-card {
            width: 100%;
          }
        }
      `}</style>
    </>
  );
};

export default ProductDisplay;
