import React from 'react';

const ProductCard = () => {
  return (
    <>
      <style>{`
        .product-card {
          border: 1px solid #ccc;
          padding: 10px;
          margin: 10px;
          width: 200px;
          text-align: center;
          border-radius: 10px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
          transition: transform 0.3s ease;
          cursor: pointer;
          display: inline-block;
          margin-right: 10px;
        }

        .product-card img {
          width: 100%;
          height: auto;
          border-radius: 8px;
          margin-bottom: 10px;
          transition: transform 0.3s ease;
          cursor: pointer;
          max-width: 100%;
          height: auto;
          object-fit: cover;
          border-radius: 10px;
        }

        .product-card button {
          background-color: #007bff;
          color: white;
          border: none;
          padding: 8px 12px;
          border-radius: 5px;
          display:;
          font-size: 14px;
          font-family: 'Roboto', sans-serif;
          display: inline-block;
          margin: 1px, 1px, 1px, 1px;
        }

        .product-card button:hover {
          background-color: #0056b3;
        }

        .product-card h3 {
        text-align: center;
        font-size: 22px;
        margin-bottom: 10px;
        }

        .product-card p{
        display: flex;
        font-size: 12px;
        font-family: 'Roboto', sans-serif;
        }
      `}</style>

      <div className="product-card">
        <img src="./Images/Men Grey Tshirt.jpg" alt="Men Grey Tshirt" />
        <h3>Men Grey Tshirt</h3>
        <p>Comfortable cotton t-shirt for men.</p>
        <button>Add to Cart</button>
        <button>Buy Now</button>
      </div>
    </>
  );
};

export default ProductCard;
