import React from "react";
import { useCart } from "../../context/CartContext";

export default function MyCart() {
  const { cart, removeFromCart, clearCart } = useCart();

  const subtotal = cart.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-8">üõí My Cart</h2>

      {cart.length === 0 ? (
        <div className="text-center py-20 border rounded-2xl">
          <p className="text-lg mb-3">Your cart is empty</p>
          <span className="text-5xl">üõçÔ∏è</span>
        </div>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">

          {/* CART ITEMS (LEFT) */}
          <div className="lg:col-span-2 space-y-5">
            {cart.map((item) => (
              <div
                key={`${item.id}-${item.size}`}
                className="flex gap-5 p-4 border rounded-2xl shadow-sm bg-white"
              >
                {/* IMAGE - LEFT */}
                <img
                  src={item.image}
                  alt={item.name}
                  className="w-28 h-32 object-cover rounded-xl"
                />

                {/* DETAILS - RIGHT */}
                <div className="flex-1 flex flex-col justify-between">
                  <div>
                    <h3 className="text-lg font-semibold">
                      {item.name}
                    </h3>
                    <p className="text-sm text-gray-500 mt-1">
                      Size: {item.size}
                    </p>
                  </div>

                  <div className="flex items-center justify-between mt-4">
                    <p className="text-sm text-gray-600">
                      ‚Çπ{item.price} √ó {item.quantity}
                    </p>
                    <p className="font-semibold text-lg">
                      ‚Çπ{item.price * item.quantity}
                    </p>
                  </div>
                </div>

                {/* REMOVE */}
                <div className="flex flex-col justify-between items-end">
                  <button
                    onClick={() => removeFromCart(item.id)}
                    className="text-sm text-red-600 hover:underline"
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* ORDER SUMMARY (RIGHT) */}
          <div className="border rounded-2xl p-6 h-fit shadow-sm bg-white">
            <h3 className="text-lg font-semibold mb-5">Order Summary</h3>

            <div className="flex justify-between mb-3">
              <span className="text-gray-600">Subtotal</span>
              <span>‚Çπ{subtotal}</span>
            </div>

            <div className="flex justify-between mb-3 text-gray-500">
              <span>Shipping</span>
              <span>Free</span>
            </div>

            <hr className="my-4" />

            <div className="flex justify-between font-semibold text-lg">
              <span>Total</span>
              <span>‚Çπ{subtotal}</span>
            </div>

            <button className="mt-6 w-full bg-black text-white py-3 rounded-xl hover:opacity-90">
              Proceed to Checkout
            </button>

            <button
              onClick={clearCart}
              className="mt-4 w-full text-sm text-gray-500 hover:underline"
            >
              Clear Cart
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
