import React from "react";
import { Link } from "react-router-dom";

const products = [
  {
    id: 1,
    name: "Men’s Classic Cotton T-Shirt",
    price: 599,
    img: "/images/Men Grey Tshirt.jpg",
  },
  {
    id: 2,
    name: "Polo T-Shirt",
    price: 799,
    img: "/images/Women Grey Tshirt.jpg",
  },
  {
    id: 3,
    name: "Graphic Tee",
    price: 699,
    img: "/images/Women Orange Tshirt.jpg",
  },
  {
    id: 4,
    name: "Oversized Tee",
    price: 899,
    img: "/images/Women White Tshirt.jpg",
  },
];

const MenTopwear = () => {
  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Men’s Topwear</h1>
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-6">
        {products.map((item) => (
          <Link
            to={`/tshirt/${item.id}`}
            key={item.id}
            className="border rounded-lg shadow-sm hover:shadow-md transition-transform transform hover:-translate-y-1"
          >
            <img
              src={item.img}
              alt={item.name}
              className="w-full h-56 object-cover rounded-t-lg"
            />
            <div className="p-3">
              <h2 className="text-sm font-semibold truncate">{item.name}</h2>
              <p className="text-gray-700 text-sm mt-1">₹{item.price}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default MenTopwear;
