import React from "react";
import { Link } from "react-router-dom";

const ProductCard = (props) => {
  let images = [];

  if (typeof props.product_images === "string") {
    try {
      const jsonFixed = props.product_images.replace(/'/g, '"');
      const parsed = JSON.parse(jsonFixed);
      images = parsed.map((obj) => Object.keys(obj)[0]);
    } catch {
      images = props.product_images.match(/https?:\/\/[^\s'"]+/g) || [];
    }
  } else if (Array.isArray(props.product_images)) {
    images = props.product_images;
  }

  return (
    <div className="product-card-item">
      <Link to={`/products/${props.id}`} className="product-link" >
        <div className="product-image-gallery">
          {images.length > 0 ? (
            <img
              src={images[0,1]}
              alt={props.productDisplayName}
              className="product-image"
              loading="lazy"
            />

          ) : (
            <div className="no-image">No image</div>
          )}
        </div>

        <div className="product-info">
          <h3 className="product-name">{props.productDisplayName}</h3>
          <p className="subcategory">{props.subCategory}</p>
          <p className="price">{props.articleType}</p>
          <p className="color">{props.baseColour}</p>
        </div>
      </Link>
    </div>
  );
};

export default ProductCard;
