    if (typeof p === "number" && Number.isFinite(p)) return p;
    if (!p) return 0;
    try {
      const s = String(p);
      const cleaned = s.replace(/[,â‚¹\s\u00A0]/g, "");
      const onlyNumDot = cleaned.replace(/[^\d.]/g, "");
      const n = parseFloat(onlyNumDot);
      return Number.isFinite(n) ? n : 0;
    } catch {
      return 0;
    }
  };

