import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const ProductDisplay = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);
  const [activeImage, setActiveImage] = useState(0);
  const [selectedSize, setSelectedSize] = useState("M");

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await fetch(`http://localhost:8000/products/${id}/`);
        if (!res.ok) throw new Error("API error");
        const data = await res.json();
        setProduct(data);
      } catch {
        setError("Product Error");
      }
    };
    fetchProduct();
  }, [id]);

  const handleAddToCart = () => {
    console.log("Add to cart:", product.id, selectedSize);
  };

  const handleWishlist = () => {
    console.log("Add to wishlist:", product.id);
  };

  const handleShare = () => {
    navigator.share
      ? navigator.share({
          title: product.product_name,
          text: product.details,
          url: window.location.href,
        })
      : alert("Sharing not supported");
  };

  if (error) return <h3>{error}</h3>;
  if (!product) return <h3>Loading...</h3>;

  return (
    <>
      <div className="product-card-item">

        {/* üñºÔ∏è Image */}
        <div className="image-container">
          <img
            src={product.images[activeImage]}
            alt={product.product_name}
            className="product-image"
          />
        </div>

        {/* Thumbnails */}
        {product.images.length > 1 && (
          <div className="thumbnail-row">
            {product.images.map((img, index) => (
              <img
                key={index}
                src={img}
                className={`thumbnail ${activeImage === index ? "active" : ""}`}
                onClick={() => setActiveImage(index)}
              />
            ))}
          </div>
        )}

        {/* Info */}
        <div className="product-info">
          <p className="product-name">{product.product_name}</p>
          <p className="gender">{product.gender}</p>
          <p className="price">${product.price}</p>
        </div>

        {/* Sizes */}
        <div className="size-selector">
          {["L", "M", "XL"].map(size => (
            <button
              key={size}
              className={selectedSize === size ? "size active" : "size"}
              onClick={() => setSelectedSize(size)}
            >
              {size}
            </button>
          ))}
        </div>

        {/* Actions */}
        <button className="btn primary" onClick={handleAddToCart}>
          Add to Cart
        </button>

        <button className="btn secondary">
          Buy Now
        </button>

        <div className="action-row">
          <button className="btn outline" onClick={handleWishlist}>
            ‚ù§Ô∏è Wishlist
          </button>

          <button className="btn outline" onClick={handleShare}>
            <svg width="18" height="18" viewBox="0 0 24 24" fill="none">
              <path
                d="M12 3v12m0-12l4 4m-4-4l-4 4M5 15v4a2 2 0 002 2h10a2 2 0 002-2v-4"
                stroke="black"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </svg>
            Share
          </button>
        </div>

        {/* Description */}
        <div className="description">
          <h4>Description</h4>
          <p>{product.details}</p>
        </div>
      </div>

      <style>{`
        .product-card-item {
          width: 360px;
          margin: 30px auto;
          padding: 20px;
          border-radius: 16px;
          background: #fff;
          box-shadow: 0 10px 25px rgba(0,0,0,0.1);
        }

        .image-container {
          height: 420px;
          border-radius: 14px;
          overflow: hidden;
        }

        .product-image {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .thumbnail-row {
          display: flex;
          gap: 8px;
          margin-top: 12px;
        }

        .thumbnail {
          width: 56px;
          height: 56px;
          border-radius: 8px;
          opacity: 0.6;
          cursor: pointer;
        }

        .thumbnail.active {
          opacity: 1;
          outline: 2px solid #000;
        }

        .product-info {
          text-align: center;
          margin-top: 16px;
        }

        .product-name {
          font-size: 20px;
          font-weight: 600;
        }

        .price {
          font-size: 18px;
          margin-top: 6px;
        }

        .size-selector {
          display: flex;
          justify-content: center;
          gap: 12px;
          margin: 18px 0;
        }

        .size {
          padding: 8px 14px;
          border-radius: 20px;
          border: 1px solid #ccc;
          background: none;
          cursor: pointer;
        }

        .size.active {
          background: #000;
          color: #fff;
          border-color: #000;
        }

        .btn {
          width: 100%;
          padding: 12px;
          border-radius: 10px;
          font-size: 16px;
          cursor: pointer;
          margin-top: 10px;
        }

        .primary {
          background: #000;
          color: #fff;
          border: none;
        }

        .secondary {
          background: #ff9800;
          border: none;
          color: #fff;
        }

        .outline {
          background: #fff;
          border: 1px solid #ccc;
          flex: 1;
        }

        .action-row {
          display: flex;
          gap: 10px;
          margin-top: 12px;
        }

        .description {
          margin-top: 20px;
          border-top: 1px solid #eee;
          padding-top: 12px;
        }

        .description h4 {
          margin-bottom: 6px;
        }
      `}</style>
    </>
  );
};

export default ProductDisplay;
