import React from 'react';

const Item = (props) => {
  return (
    <div className="item" style={styles.item}>
      <img 
        src={props.image.startsWith("http") ? props.image : `/Data/Images/${props.image}`} 
        alt={props.category || "Product"} 
        style={styles.image}
      />
      <p style={styles.name}>{props.category}</p>
      <div style={styles.type}>{props.type}</div>
    </div>
  );
};

const styles = {
  item: {
    width: "220px",
    backgroundColor: "#fff",
    borderRadius: "10px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
    overflow: "hidden",
    textAlign: "center",
    padding: "10px",
    transition: "transform 0.3s ease, box-shadow 0.3s ease",
  },
  image: {
    width: "100%",
    height: "250px",
    objectFit: "cover",
    borderRadius: "10px",
  },
  name: {
    fontSize: "1rem",
    fontWeight: "600",
    color: "#333",
    marginTop: "10px",
  },
  type: {
    fontSize: "0.9rem",
    color: "#ff6f61",
    fontWeight: "500",
  },
};

export default Item;
