import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { useCart } from "../../context/CartContext";


const ProductDisplay = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);
  const [activeImage, setActiveImage] = useState(0);
  const [selectedSize, setSelectedSize] = useState("M");
  const { addToCart } = useCart();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await fetch(`http://localhost:8000/products/${id}/`);
        if (!res.ok) throw new Error("API error");
        const data = await res.json();
        setProduct(data);
      } catch {
        setError("Product Error");
      }
    };
    fetchProduct();
  }, [id]);

  if (error) return <h3>{error}</h3>;
  if (!product) return <h3>Loading...</h3>;


  const handleAddToCart = () => {
    const cartItem = {
      id: product.id,
      name: product.product_name,
      price: product.price,
      image: product.images[0],
      size: selectedSize,
      quantity: 1,
    };

    addToCart(cartItem);
    alert("Added to cart");
  };


  return (
    <>
      <div className="product-wrapper">

        {/* IMAGE CARD */}
        <div className="image-card">
          <div className="image-container">
            <img
              src={product.images[activeImage]}
              alt={product.product_name}
              className="product-image"
            />
          </div>

          {product.images.length > 1 && (
            <div className="thumbnail-row">
              {product.images.map((img, index) => (
                <img
                  key={index}
                  src={img}
                  className={`thumbnail ${activeImage === index ? "active" : ""}`}
                  onClick={() => setActiveImage(index)}
                />
              ))}
            </div>
          )}
        </div>

        {/* PRODUCT DETAILS */}
        <div className="product-details">

          <h2 className="product-name">{product.product_name}</h2>
          <p className="gender">{product.gender}</p>
          <p className="price">${product.price}</p>

          {/* SIZE */}
          <div className="size-selector">
            {["L", "M", "XL"].map(size => (
              <button
                key={size}
                className={`size ${selectedSize === size ? "active" : ""}`}
                onClick={() => setSelectedSize(size)}
              >
                {size}
              </button>
            ))}
          </div>

          {/* ACTIONS */}
          <Link to="/MyCart" className="text-decoration-none">
          <button className="btn primary" onClick={handleAddToCart}>
            Add to Cart</button>
          </Link>

          <button className="btn secondary">Buy Now</button>

          <div className="action-row">
            <button className="btn outline">❤️ Wishlist</button>
            <button className="btn outline">
              <svg width="18" height="18" viewBox="0 0 24 24">
                <path
                  d="M12 3v12m0-12l4 4m-4-4l-4 4M5 15v4a2 2 0 002 2h10a2 2 0 002-2v-4"
                  stroke="black"
                  strokeWidth="2"
                  fill="none"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
              </svg>
              Share
            </button>
          </div>

          {/* DESCRIPTION */}
          <div className="description">
            <h4>Description</h4>
            <p>{product.details}</p>
          </div>

        </div>
      </div>

      <style>{`
        .product-wrapper {
          display: flex;
          gap: 32px;
          max-width: 1000px;
          margin: 40px auto;
          padding: 20px;
        }

        /* IMAGE CARD */
        .image-card {
          width: 420px;
          padding: 16px;
          border-radius: 18px;
          background: #fff;
          box-shadow: 0 12px 30px rgba(0,0,0,0.12);
        }

        .image-container {
          height: 460px;
          border-radius: 14px;
          overflow: hidden;
        }

        .product-image {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .thumbnail-row {
          display: flex;
          gap: 10px;
          margin-top: 14px;
          justify-content: center;
        }

        .thumbnail {
          width: 60px;
          height: 60px;
          border-radius: 8px;
          opacity: 0.6;
          cursor: pointer;
        }

        .thumbnail.active {
          opacity: 1;
          outline: 2px solid #000;
        }

        /* DETAILS */
        .product-details {
          flex: 1;
        }

        .product-name {
          font-size: 26px;
          margin-bottom: 6px;
        }

        .gender {
          color: #777;
          margin-bottom: 10px;
        }

        .price {
          font-size: 22px;
          font-weight: 600;
          margin-bottom: 20px;
        }

        .size-selector {
          display: flex;
          gap: 12px;
          margin-bottom: 24px;
        }

        .size {
          padding: 10px 16px;
          border-radius: 24px;
          border: 1px solid #ccc;
          background: none;
          cursor: pointer;
        }

        .size.active {
          background: #000;
          color: #fff;
          border-color: #000;
        }

        .btn {
          width: 100%;
          padding: 14px;
          border-radius: 12px;
          font-size: 16px;
          cursor: pointer;
          margin-bottom: 12px;
        }

        .primary {
          background: #000;
          color: #fff;
          border: none;
        }

        .secondary {
          background: #ff9800;
          border: none;
          color: #fff;
        }

        .outline {
          background: #fff;
          border: 1px solid #ccc;
          flex: 1;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 6px;
        }

        .action-row {
          display: flex;
          gap: 12px;
          margin-bottom: 20px;
        }

        .description {
          border-top: 1px solid #eee;
          padding-top: 16px;
        }

        /* MOBILE */
        @media (max-width: 768px) {
          .product-wrapper {
            flex-direction: column;
            align-items: center;
          }

          .image-card {
            width: 100%;
          }
        }
      `}</style>
    </>
  );
};

export default ProductDisplay;
