import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const UpdateProduct = () => {
  const location = useLocation();
  const preloadedId = location.state?.productId || "";
  const [productId, setProductId] = useState(preloadedId);
  const [product, setProduct] = useState({
    productDisplayName: "",
    gender: "",
    masterCategory: "",
    subCategory: "",
    articleType: "",
    baseColour: "",
    season: "",
    year: "",
    usage: "",
  });
  const [loading, setLoading] = useState(false);
  const [updating, setUpdating] = useState(false);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(!!preloadedId);

  // üîπ Fetch product details when ID is provided (either from navigation or user input)
  useEffect(() => {
    if (!productId || isNaN(productId)) return;
    const fetchProduct = async () => {
      setLoading(true);
      setError("");
      try {
        const response = await fetch(`http://localhost:8000/get_product/${productId}/`);
        if (!response.ok) throw new Error(`HTTP ${response.status}`);
        const data = await response.json();
        setProduct(data);
        setShowForm(true);
      } catch (err) {
        console.error("‚ùå Error fetching product:", err);
        setError("Product not found or failed to fetch details.");
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [productId]);

  const handleChange = (e) => {
    setProduct({
      ...product,
      [e.target.name]: e.target.value,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    setUpdating(true);
    setError("");

    try {
      const updatedProduct = { ...product };
      delete updatedProduct.id; // backend takes id from URL
      if (updatedProduct.year) updatedProduct.year = Number(updatedProduct.year);

      const response = await fetch(`http://localhost:8000/update_product/${productId}/`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedProduct),
      });

      if (!response.ok) {
        const text = await response.text();
        throw new Error(text);
      }

      const data = await response.json();
      alert(`‚úÖ ${data.message}`);
    } catch (err) {
      console.error("‚ùå Update error:", err);
      setError("Failed to update product. Please check your data.");
    } finally {
      setUpdating(false);
    }
  };

  return (
    <div className="update-container">
      <h2>Update Product</h2>

      {!showForm && (
        <div className="id-section">
          <label htmlFor="productId">Enter Product ID:</label>
          <input
            type="number"
            id="productId"
            value={productId}
            onChange={(e) => setProductId(e.target.value)}
            placeholder="e.g. 1234"
          />
        </div>
      )}

      {loading && <p>Loading product...</p>}
      {error && <div className="error-msg">{error}</div>}

      {showForm && !loading && (
        <form onSubmit={handleUpdate}>
          {Object.keys(product).map((key) => (
            <div key={key} className="form-group">
              <label htmlFor={key}>{key}</label>
              <input
                type="text"
                id={key}
                name={key}
                value={product[key] || ""}
                onChange={handleChange}
              />
            </div>
          ))}

          <button type="submit" disabled={updating}>
            {updating ? "Updating..." : "Update Product"}
          </button>
        </form>
      )}

      <style>{`
        .update-container {
          max-width: 600px;
          margin: 40px auto;
          padding: 20px;
          background: #ffffff;
          border-radius: 12px;
          box-shadow: 0 0 10px rgba(0,0,0,0.1);
        }
        h2 {
          text-align: center;
          margin-bottom: 20px;
        }
        .id-section {
          display: flex;
          flex-direction: column;
          margin-bottom: 20px;
        }
        .id-section input {
          padding: 10px;
          border: 1px solid #ccc;
          border-radius: 8px;
          font-size: 16px;
        }
        .form-group {
          margin-bottom: 15px;
        }
        label {
          display: block;
          margin-bottom: 5px;
          text-transform: capitalize;
          font-weight: 600;
        }
        input {
          width: 100%;
          padding: 10px;
          border: 1px solid #ccc;
          border-radius: 8px;
          font-size: 15px;
        }
        button {
          width: 100%;
          padding: 12px;
          border: none;
          background-color: #007bff;
          color: white;
          border-radius: 8px;
          cursor: pointer;
          font-size: 16px;
          transition: background 0.2s;
        }
        button:hover {
          background-color: #0056b3;
        }
        .error-msg {
          color: red;
          margin-bottom: 15px;
          font-weight: 500;
        }
      `}</style>
    </div>
  );
};

export default UpdateProduct;
