import React, { useContext } from "react";
import { ShopContext } from "../context/Shopcontext";
import Item from "./Item";

const Shop = (props) => {
  const { allproduct } = useContext(ShopContext);

  // 🖼️ Banner images for each category
  const bannerImages = {
    MEN: process.env.PUBLIC_URL + "/Data/Images/Men Banner.jpg",
    WOMEN: process.env.PUBLIC_URL + "/Data/Images/Women Banner.jpg",
    KIDS: process.env.PUBLIC_URL + "/Data/Images/Kids Banner.jpg",
  };

  // 💅 Inline styles
  const styles = {
    container: {
      textAlign: "center",
      fontFamily: "Poppins, sans-serif",
      backgroundColor: "#fafafa",
      paddingBottom: "40px",
    },
    bannerSection: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      background: "linear-gradient(90deg, #fff6f6, #ffeaea)",
      borderRadius: "12px",
      boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
      padding: "30px 50px",
      margin: "30px auto",
      maxWidth: "1200px",
      flexWrap: "wrap",
    },
    bannerText: {
      flex: "1",
      textAlign: "left",
      paddingRight: "30px",
      minWidth: "300px",
    },
    bannerTitle: {
      fontSize: "2.5rem",
      fontWeight: "700",
      color: "#222",
      marginBottom: "10px",
    },
    bannerSubtitle: {
      fontSize: "1.1rem",
      color: "#444",
      marginBottom: "20px",
    },
    bannerButton: {
      backgroundColor: "#ff6f61",
      color: "#fff",
      border: "none",
      padding: "12px 24px",
      borderRadius: "25px",
      fontSize: "1rem",
      cursor: "pointer",
      fontWeight: "600",
      transition: "all 0.3s ease",
    },
    bannerButtonHover: {
      backgroundColor: "#e65550",
    },
    bannerImage: {
      width: "350px",
      height: "300px",
      objectFit: "cover",
      borderRadius: "12px",
      boxShadow: "0 4px 10px rgba(0,0,0,0.2)",
    },
    header: {
      display: "flex",
      flexWrap: "wrap",
      justifyContent: "center",
      alignItems: "center",
      gap: "15px",
      marginBottom: "20px",
      padding: "15px",
      backgroundColor: "#fff",
      borderRadius: "8px",
      boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    },
    productGrid: {
      display: "flex",
      flexWrap: "wrap",
      justifyContent: "center",
      gap: "20px",
    },
    selectBox: {
      padding: "5px",
      borderRadius: "5px",
      border: "1px solid #ccc",
    },
  };

  // Dynamic banner text per category
  const categoryText = {
    MEN: {
      title: "Men’s Collection",
      subtitle: "Aesthetic Men’s Wear for Every Occasion",
    },
    WOMEN: {
      title: "Women’s Collection",
      subtitle: "Chic, Elegant & Trendy Styles for You",
    },
    KIDS: {
      title: "Kids’ Collection",
      subtitle: "Cute, Comfy & Colorful Fashion for Little Stars",
    },
  };

  return (
    <div style={styles.container}>
      {/* 🖼️ Banner Section */}
      <div style={styles.bannerSection}>
        <div style={styles.bannerText}>
          <h1 style={styles.bannerTitle}>{categoryText[props.TYPE]?.title}</h1>
          <p style={styles.bannerSubtitle}>
            {categoryText[props.TYPE]?.subtitle}
          </p>
          <button
            style={styles.bannerButton}
            onMouseOver={(e) =>
              (e.target.style.backgroundColor = "#e65550")
            }
            onMouseOut={(e) =>
              (e.target.style.backgroundColor = "#ff6f61")
            }
          >
            Shop Now →
          </button>
        </div>

        <img
          src={bannerImages[props.TYPE]}
          alt={`${props.TYPE} banner`}
          style={styles.bannerImage}
        />
      </div>

      {/* 🔽 Sorting / Header Section */}
      <div style={styles.header}>
        <span>Showing 1 - 16 Products</span>
        <span>Total Products: 54000</span>
        <label>
          Sort by{" "}
          <select style={styles.selectBox}>
            <option>Featured</option>
            <option>Price</option>
            <option>Name</option>
          </select>
        </label>
        <label>
          Show{" "}
          <select style={styles.selectBox}>
            <option>16</option>
            <option>20</option>
            <option>24</option>
          </select>
        </label>
        <label>
          View{" "}
          <select style={styles.selectBox}>
            <option>Grid</option>
            <option>List</option>
          </select>
        </label>
      </div>

      {/* 🛍️ Product Grid */}
      <div style={styles.productGrid}>
        {allproduct.map((item, index) => {
          if (props.TYPE === item.TYPE) {
            return (
              <Item
                key={index}
                image={item.image}
                category={item.displayname}
                type={item.TYPE}
              />
            );
          }
          return null;
        })}
      </div>
    </div>
  );
};

export default Shop;
