import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ProductDisplay from './product';

const Item = () => {
  const { id } = useParams();
  const [productId, setProductId] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:8000/products/${id}`)
    .then ((res) => {
      if (!res.ok) throw new Error("Failed to fetch product");
      return res.json();
    })
    .then ((data) => {
      setProductId(data);
    })
    .catch((err) => {
      console.log(err);
    });
  },[id])

  if (!id) {
    return <div>Product not found</div>;
  }

  console.log(productId.product_name);

  let images = [];
  const { product_images } = productId;

  if (typeof product_images === "string") {
    try {
      const jsonFixed = product_images.replace(/'/g, '"');
      const parsed = JSON.parse(jsonFixed);
      images = parsed.map((obj) => Object.keys(obj)[0]);
    } catch {
      images = product_images.match(/https?:\/\/[^\s'"]+/g) || [];
    }
  } else if (Array.isArray(product_images)) {
    images = product_images;
  }

  return (
    <div className="product-page">
      <ProductDisplay product={productId} />
    </div>
  );
};

export default Item;
