import React, { Component } from "react";

export default class Home extends Component {
  render() {
    return (
      <>
      <style>{`
        .card {
          border: 1px solid #ccc;
          width: max;
          text-align: left;
          border-radius: 10px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
          transition: transform 0.3s ease;
          cursor: pointer;
          display: inline-block;
          vertical-align: top;
          margin: 10px;
          background_color: pink;
          height: 10px;
      }
          .card:hover {
          transform: scale(1.05);
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
        }
          .card img {
          max-width: 100%;
          height: auto;
        }
          .card h1 {
          font-size: 1.5rem;
          margin-bottom: 10px;
        }
          .card h2 {
          font-size: 1rem;
          margin-bottom: 10px;
        }
          .card p {
          font-size: 0.9rem;
          margin-bottom: 10px;
        }

        `}



      </style>
      <div className="container my-5">
        <div className="row g-4 justify-content-center">
          <div className="card">
            <h1>Biggest Diwali Sale</h1>
            <h2>This Diwali get upto 80% discount on your favourite Attire.</h2>
            <img src="/Data/Images/BiggestDiwaliSale.png" alt="Biggest Diwali Sale" className="card-img-top" />
          </div>
          </div>
      </div>
      </>
    );
  }
}
