import React, { useContext, useState, useMemo } from "react";
import { ShopContext } from "../context/Shopcontext";
import ProductCard from "./Product Card";

const Shop = (props) => {
  const { cleaned_zara_all_data } = useContext(ShopContext);

  const [sortBy, setSortBy] = useState("Featured");
  const [itemsPerPage, setItemsPerPage] = useState(16);
  const [viewType, setViewType] = useState("Grid");

  const bannerImages = {
    Men: process.env.PUBLIC_URL + "/Data/Images/Men Banner.jpg",
    Woman: process.env.PUBLIC_URL + "/Data/Images/Women Banner.jpg",
    Kids: process.env.PUBLIC_URL + "/Data/Images/Kids Banner.jpg",
  };

  const categoryText = {
    Men: {
      title: "Men‚Äôs Collection",
      subtitle: "Aesthetic Men‚Äôs Wear for Every Occasion",
    },
    Woman: {
      title: "Woman‚Äôs Collection",
      subtitle: "Chic, Elegant & Trendy Styles for You",
    },
    Kids: {
      title: "Kids‚Äô Collection",
      subtitle: "Cute, Comfy & Colorful Fashion for Little Stars",
    },
  };

  const filteredData = cleaned_zara_all_data.filter(
    (item) => item.category === props.category
  );

  const sortedData = useMemo(() => {
    let sorted = [...filteredData];
    if (sortBy === "Price") {
      sorted.sort((a, b) => (a.price || 0) - (b.price || 0));
    } else if (sortBy === "Name") {
      sorted.sort((a, b) => a.product_name.localeCompare(b.product_name));
    }
    return sorted;
  }, [filteredData, sortBy]);

  const paginatedData = sortedData.slice(0, itemsPerPage);

  return (
    <div className="shop-container">
      <style>{`
        .shop-container {
          text-align: center;
          font-family: "Poppins", sans-serif;
          padding-bottom: 40px;
          overflow-x: hidden; /* ‚úÖ Prevent horizontal scroll */
        }

        .banner-section {
          display: flex;
          justify-content: space-between;
          align-items: center;
          background-image: linear-gradient(to right, rgba(245, 233, 221, 0.85), rgba(222, 220, 220, 0.7)),
            url("/Data/Images/Diwali.jpg");
          border-radius: 12px;
          box-shadow: 0 4px 10px rgba(0,0,0,0.1);
          padding: 30px 50px;
          margin: 30px auto;
          max-width: 1200px;
          flex-wrap: wrap;
        }

        .banner-text {
          flex: 1;
          text-align: left;
          padding-right: 30px;
          min-width: 300px;
        }

        .banner-title {
          font-size: 2rem;
          font-weight: 700;
          color: #222;
          margin-bottom: 10px;
        }

        .banner-subtitle {
          font-size: 1rem;
          color: #444;
          margin-bottom: 20px;
        }

        .banner-button {
          background-color: #ff6f61;
          color: #fff;
          border: none;
          padding: 10px 20px;
          border-radius: 25px;
          font-size: 0.95rem;
          cursor: pointer;
          font-weight: 600;
          transition: all 0.3s ease;
        }

        .banner-button:hover {
          background-color: #e65550;
        }

        .banner-image {
          width: 280px;
          height: 220px;
          object-fit: cover;
          border-radius: 12px;
          box-shadow: 0 4px 10px rgba(0,0,0,0.2);
        }

        .shop-header {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          align-items: center;
          gap: 15px;
          margin-bottom: 20px;
          padding: 10px;
          background-color: #fff;
          border-radius: 8px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }

        .select-box {
          padding: 5px;
          border-radius: 5px;
          border: 1px solid #ccc;
          font-family: inherit;
        }

        /* ‚úÖ FIXED GRID: Smaller Cards, No Scroll */
        .product-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
          gap: 18px;
          max-width: 1100px;
          margin: 0 auto;
          padding: 0 15px;
        }

        /* üìã List View */
        .product-list {
          display: flex;
          flex-direction: column;
          gap: 15px;
          max-width: 900px;
          margin: 0 auto;
          padding: 0 15px;
        }

        /* ‚úÖ Compact Card Style */
        .product-card-item {
          background-image: linear-gradient(to right, rgba(245, 233, 221, 0.85), rgba(222, 220, 220, 0.7)),
            url("/Data/Images/Diwali.jpg");
          border-radius: 10px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
          padding: 10px;
          text-align: center;
          transition: transform 0.2s ease;
        }

        .product-card-item:hover {
          transform: translateY(-4px);
        }

        .product-card-item img {
          width: 100%;
          height: 160px;
          object-fit: cover;
          border-radius: 8px;
          margin-bottom: 8px;
        }

        .product-card-item h3 {
          font-size: 0.95rem;
          margin: 5px 0;
        }

        .product-card-item p {
          font-size: 0.85rem;
          color: #555;
          margin: 0;
        }

        @media (max-width: 768px) {
          .banner-section {
            flex-direction: column;
            text-align: center;
            padding: 20px;
          }
          .banner-image {
            width: 90%;
            height: auto;
            margin-top: 15px;
          }
        }
      `}</style>

      {/* üñºÔ∏è Banner */}
      <div className="banner-section">
        <div className="banner-text">
          <h1 className="banner-title">{categoryText[props.category]?.title}</h1>
          <p className="banner-subtitle">{categoryText[props.category]?.subtitle}</p>
          <button className="banner-button">Shop Now ‚Üí</button>
        </div>
        <img
          src={bannerImages[props.category]}
          alt={`${props.category} banner`}
          className="banner-image"
        />
      </div>

      {/* ‚öôÔ∏è Header */}
      <div className="shop-header">
        <span>
          Showing 1 - {Math.min(itemsPerPage, sortedData.length)} of{" "}
          {sortedData.length} Products
        </span>

        <label>
          Sort by{" "}
          <select
            className="select-box"
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option>Featured</option>
            <option>Price</option>
            <option>Name</option>
          </select>
        </label>

        <label>
          Show{" "}
          <select
            className="select-box"
            value={itemsPerPage}
            onChange={(e) => setItemsPerPage(Number(e.target.value))}
          >
            <option value={16}>16</option>
            <option value={20}>20</option>
            <option value={24}>24</option>
          </select>
        </label>

        <label>
          View{" "}
          <select
            className="select-box"
            value={viewType}
            onChange={(e) => setViewType(e.target.value)}
          >
            <option>Grid</option>
            <option>List</option>
          </select>
        </label>
      </div>

      {/* üõçÔ∏è Product Display */}
      <div className={viewType === "Grid" ? "product-grid" : "product-list"}>
        {paginatedData.map((item) => (
          <div className="product-card-item" key={item.id}>
            <ProductCard {...item} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Shop;
