import React, { useContext } from "react";
import { ShopContext } from "../context/Shopcontext";
import ProductCard from "./Product Card";

const Shop = (props) => {
  const { cleaned_zara_all_data } = useContext(ShopContext);

  // Banner images
  const bannerImages = {
    Men: process.env.PUBLIC_URL + "/Data/Images/Men Banner.jpg",
    Woman: process.env.PUBLIC_URL + "/Data/Images/Women Banner.jpg",
    Kids: process.env.PUBLIC_URL + "/Data/Images/Kids Banner.jpg",
  };

  // Dynamic banner text per category
  const categoryText = {
    Men: {
      title: "Men’s Collection",
      subtitle: "Aesthetic Men’s Wear for Every Occasion",
    },
    Woman: {
      title: "Woman’s Collection",
      subtitle: "Chic, Elegant & Trendy Styles for You",
    },
    Kids: {
      title: "Kids’ Collection",
      subtitle: "Cute, Comfy & Colorful Fashion for Little Stars",
    },
  };

  return (
    <div className="shop-container">
      <style>{`
        .shop-container {
          text-align: center;
          font-family: "Poppins", sans-serif;
          background-image: linear-gradient(to right, rgba(245, 233, 221, 0.85), rgba(222, 220, 220, 0.7)),
            url("/Data/Images/Diwali.jpg");
          padding-bottom: 40px;
        }

        .banner-section {
          display: flex;
          justify-content: space-between;
          align-items: center;
          background: linear-gradient(90deg, #fff6f6, #ffeaea);
          border-radius: 12px;
          box-shadow: 0 4px 10px rgba(0,0,0,0.1);
          padding: 30px 50px;
          margin: 30px auto;
          max-width: 1200px;
          flex-wrap: wrap;
        }

        .banner-text {
          flex: 1;
          text-align: left;
          padding-right: 30px;
          min-width: 300px;
        }

        .banner-title {
          font-size: 2.5rem;
          font-weight: 700;
          color: #222;
          margin-bottom: 10px;
        }

        .banner-subtitle {
          font-size: 1.1rem;
          color: #444;
          margin-bottom: 20px;
        }

        .banner-button {
          background-color: #ff6f61;
          color: #fff;
          border: none;
          padding: 12px 24px;
          border-radius: 25px;
          font-size: 1rem;
          cursor: pointer;
          font-weight: 600;
          transition: all 0.3s ease;
        }

        .banner-button:hover {
          background-color: #e65550;
        }

        .banner-image {
          width: 350px;
          height: 300px;
          object-fit: cover;
          border-radius: 12px;
          box-shadow: 0 4px 10px rgba(0,0,0,0.2);
        }

        .shop-header {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          align-items: center;
          gap: 15px;
          margin-bottom: 20px;
          padding: 15px;
          background-color: #fff;
          border-radius: 8px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }

        .select-box {
          padding: 5px;
          border-radius: 5px;
          border: 1px solid #ccc;
          font-family: inherit;
        }

        .product-grid {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          gap: 20px;
          padding: 0 20px;
        }

        /* ✅ Product Card Styles */
        .product-card-item {
          flex: 1 1 calc(25% - 20px); /* 4 cards per row */
          background-color: #fff;
          border-radius: 12px;
          box-shadow: 0 2px 8px rgba(0,0,0,0.1);
          padding: 15px;
          text-align: center;
          transition: transform 0.2s ease;
        }

        .product-card-item:hover {
          transform: translateY(-5px);
        }

        .product-card-item img {
          width: 100%;
          height: 180px;
          object-fit: cover;
          border-radius: 10px;
          margin-bottom: 10px;
        }

        .product-card-item h3 {
          font-size: 1rem;
          margin: 0 0 5px;
        }

        .product-card-item p {
          font-size: 0.9rem;
          color: #555;
          margin: 0;
        }

        @media (max-width: 768px) {
          .product-card-item {
            flex: 1 1 calc(50% - 20px); /* 2 cards per row */
          }
        }

        @media (max-width: 480px) {
          .product-card-item {
            flex: 1 1 100%; /* full width on small screens */
          }
        }

        @media (max-width: 768px) {
          .banner-section {
            flex-direction: column;
            text-align: center;
          }

          .banner-text {
            padding-right: 0;
          }

          .banner-image {
            width: 90%;
            height: auto;
            margin-top: 20px;
          }
        }
      `}</style>

      {/* Banner Section */}
      <div className="banner-section">
        <div className="banner-text">
          <h1 className="banner-title">{categoryText[props.category]?.title}</h1>
          <p className="banner-subtitle">{categoryText[props.category]?.subtitle}</p>
          <button className="banner-button">Shop Now →</button>
        </div>
        <img
          src={bannerImages[props.category]}
          alt={`${props.category} banner`}
          className="banner-image"
        />  
      </div>

      {/* Header Section */}
      <div className="shop-header">
        <span>Showing 1 - 16 Products</span>
        <span>Total Products: 54000</span>

        <label>
          Sort by{" "}
          <select className="select-box">
            <option>Featured</option>
            <option>Price</option>
            <option>Name</option>
          </select>
        </label>

        <label>
          Show{" "}
          <select className="select-box">
            <option>16</option>
            <option>20</option>
            <option>24</option>
          </select>
        </label>

        <label>
          View{" "}
          <select className="select-box">
            <option>Grid</option>
            <option>List</option>
          </select>
        </label>
      </div>

      {/* Product Grid */}
      <div className="product-grid">
        {cleaned_zara_all_data
          .filter((item) => item.category === props.category)
          .map((item) => (
            <ProductCard key={item.id} {...item} />
          ))}
      </div>
    </div>
  );
};

export default Shop;
