import React, { useState, useEffect } from "react";
import ProductCard from "./Product Card"; // ✅ Make sure the file name matches exactly (no space)

const Shop = ({ gender }) => {
  const [products, setProducts] = useState([]);

  // ✅ Fetch data from FastAPI
  useEffect(() => {
    fetch("http://localhost:8000/products/")
      .then((res) => {
        if (!res.ok) throw new Error(`HTTP error: ${res.status}`);
        return res.json();
      })
      .then((data) => {
        console.log("✅ All products fetched:", data);
        setProducts(data);
      })
      .catch((err) => {
        console.error("❌ Error fetching products:", err);
      });
  }, []);

  // ✅ Filter products by gender (case-insensitive)
  const filteredProducts = products.filter(
    (p) => p.gender && p.gender.toLowerCase() === gender.toLowerCase()
  );

  return (
    <>
    <div className="shop-container">
      <h1 className="shop-title">{gender} Collection</h1>

      <div className="product-list">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((products) => (
            <ProductCard key={products.id} {...products} />
          ))
        ) : (
          <p>No products found for {gender}.</p>
        )}
      </div>
    </div>
   <style>{`
        .top-banner {
          position: relative;
          display: flex;
          justify-content: flex-start;
          align-items: center;
          padding: 40px;
          border-radius: 15px;
          margin: 40px auto;
          width: 95%;
          height: 300px;
          background-image: 
            linear-gradient(to right, rgba(245, 233, 221, 0.85), rgba(222, 220, 220, 0.7)),
            url("/Data/Images/Diwali.jpg");
          background-size: 100% 200%; /* This ensures we show only half vertically */
          background-position: top center; /* show the top half */
          background-repeat: no-repeat;

          box-shadow: 0 4px 15px rgba(0,0,0,0.1);
          overflow: hidden;
        }

        .top-banner-content {
          flex: 1;
          z-index: 1;
          text-align: left;
        }

        .top-banner-content h1 {
          font-size: 2.5rem;
          color: #222;
          margin-bottom: 10px;
          font-weight: 700;
        }

        .top-banner-content p {
          font-size: 1.1rem;
          color: #333;
          margin-bottom: 15px;
        }

        .Limited_Time {
          font-size: 0.9rem;
          color: #1f0301;
          margin-bottom: 20px;
          font-weight: 600;
        }

        .shop-now-btn {
          background-color: #3b3b3b;
          color: white;
          padding: 10px 25px;
          border-radius: 25px;
          text-decoration: none;
          font-weight: bold;
          transition: all 0.3s ease;
        }

        .shop-now-btn:hover {
          background-color: #000;
          transform: scale(1.05);
        }

          .banner-image {
            width: 100%;
            max-width: 400px;
            border-radius: 15px;
            object-fit: cover;
            object-position: center top; /* <– this “cuts” from the top */
            box-shadow: 0 4px 15px rgba(0,0,0,0.2);
            max-height: 100%;
          }

          .Newsletter-card {
            text-align: center;
            background-image: linear-gradient(to right, rgba(245, 233, 221, 0.85), rgba(222, 220, 220, 0.7)),
            url("/Data/Images/Diwali.jpg");
            padding: 30px;
            border-radius: 15px;
            width: 80%;
            margin: 40px auto;
          }

          .Newsletter-card input {
            padding: 10px;
            border-radius: 5px;
            border: 1px solid #ccc;
            width: 250px;
            margin-right: 10px;
          }

          .Newsletter-card button {
            background-color: #000000ff;
            color: white;
            border: none;
            border-radius: 5px;
            padding: 10px 20px;
            font-weight: bold;
            cursor: pointer;
          }

