            box-shadow: 0 6px 18px rgba(0, 0, 0, 0.15);
          }

          .product-image-gallery {
            width: 100%;
            height: 280px;
            overflow: hidden;
            display: flex;
            justify-content: center;
            align-items: center;
          }

          .product-image-gallery img {
            width: 100%;
            height: 100%;
            object-fit: cover;
          }

          .product-info {
            padding: 12px 16px;
          }

          .product-name {
            font-family: 'Times Roman', sans-serif;
            width: 100%;
            padding: 10px;
            border-radius: 5px;
            color: #000000ff;
            font-weight: bold;
            font-size: 1rem;
            text-decoration: none;
            transition: background-color 0.3s ease, transform 0.3s ease;
            cursor: pointer;
          }

          .subcategory {
            font-size: 0.85rem;
            color: #666;
            margin-bottom: 4px;
          }

          .category {
            font-size: 0.8rem;
            color: #999;
            margin-bottom: 8px;
          }

          .price {
            font-size: 1rem;
            font-weight: bold;
            color: #000000ff;
          }

          /* 🧱 Container Adjustments */
          .product-card {
            text-align: center;
            padding: 40px 0;
            flex-direction: column;
          }

          .product-card h1 {
            margin-bottom: 20px;
            font-size: 2rem;
          }

          .product-list {
            display: flex;
            flex-wrap: wrap;
            justify-content: center;
            gap: 25px;
          }
        `}</style>
        </>
   );
};

export default Shop;
