import React, { useState, useEffect } from "react";
import ProductCard from "./Product Card"; // ✅ Make sure name matches your file

const Shop = ({ gender }) => {
  const [products, setProducts] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 20;

  // ✅ Fetch products when gender or page changes
  useEffect(() => {
    const skip = (currentPage - 1) * productsPerPage;
    const url = `http://localhost:8000/products/?skip=${skip}&limit=${productsPerPage}&gender=${gender}`;

    fetch(url)
      .then((res) => {
        if (!res.ok) throw new Error(`HTTP error: ${res.status}`);
        return res.json();
      })
      .then((data) => {
        console.log("✅ Products fetched:", data);
        setProducts(data.data || []);
        setTotalPages(data.pages || 1);
      })
      .catch((err) => {
        console.error("❌ Error fetching products:", err);
      });
  }, [gender, currentPage]);

  const handlePageChange = (event) => {
    setCurrentPage(Number(event.target.value));
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <>
      <div className="shop-container">
        <h1 className="shop-title">{gender} Collection</h1>

        <div className="product-list">
          {products.length > 0 ? (
            products.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))
          ) : (
            <p>No products found for {gender}.</p>
          )}
        </div>

        {/* 🧭 Pagination Dropdown */}
        {totalPages > 1 && (
          <div className="pagination-container">
            <label htmlFor="page-select">Page:</label>
            <select
              id="page-select"
              value={currentPage}
              onChange={handlePageChange}
            >
              {Array.from({ length: totalPages }, (_, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>
            <span>
              &nbsp;of {totalPages}
            </span>
          </div>
        )}
      </div>

      {/* 💅 Styles */}
      <style>{`
        .shop-container {
          padding: 20px;
          text-align: center;
        }

        .shop-title {
          margin-bottom: 20px;
          font-size: 2rem;
          text-transform: capitalize;
        }

        .product-list {
          display: flex;
          flex-wrap: wrap;
          gap: 20px;
          justify-content: center;
        }

        .pagination-container {
          margin-top: 30px;
          display: flex;
          justify-content: center;
          align-items: center;
          font-size: 1rem;
          gap: 8px;
        }

        select {
          padding: 5px 10px;
          font-size: 1rem;
          border-radius: 5px;
          border: 1px solid #ccc;
          cursor: pointer;
        }

        select:hover {
          border-color: #000;
        }
      `}</style>
    </>
  );
};

export default Shop;
