import React, { useState, useEffect } from "react";
import ProductCard from "./Product Card"; // âœ… Ensure filename matches

const Shop = ({ gender }) => {
  const [products, setProducts] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const productsPerPage = 20;

  // âœ… Fetch products whenever gender or page changes
  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      setError("");

      const skip = (currentPage - 1) * productsPerPage;
      const url = `http://localhost:8000/products/?skip=${skip}&limit=${productsPerPage}&gender=${gender}`;

      try {
        const res = await fetch(url);
        if (!res.ok) throw new Error(`HTTP error: ${res.status}`);

        const data = await res.json();
        console.log("âœ… Products fetched:", data);

        setProducts(data.data || []);
        setTotalPages(data.pages || 1);
      } catch (err) {
        console.error("âŒ Error fetching products:", err);
        setError("Failed to load products. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [gender, currentPage]);

  // âœ… Reset to page 1 when gender changes
  useEffect(() => {
    setCurrentPage(1);
  }, [gender]);

  const handlePageChange = (event) => {
    setCurrentPage(Number(event.target.value));
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handlePrevPage = () => {
    if (currentPage > 1) setCurrentPage((prev) => prev - 1);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) setCurrentPage((prev) => prev + 1);
  };

  return (
    <>
      <div className="shop-container">
        <h1 className="shop-title">{gender} Collection</h1>

        {loading ? (
          <p>Loading products...</p>
        ) : error ? (
          <p className="error-message">{error}</p>
        ) : products.length > 0 ? (
          <div className="product-list">
            {products.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))}
          </div>
        ) : (
          <p>No products found for {gender}.</p>
        )}

        {/* ðŸ§­ Pagination Controls */}
        {totalPages > 1 && (
          <div className="pagination-container">
            <button
              onClick={handlePrevPage}
              disabled={currentPage === 1}
              className="pagination-btn"
            >
              â¬… Prev
            </button>

            <label htmlFor="page-select">Page:</label>
            <select
              id="page-select"
              value={currentPage}
              onChange={handlePageChange}
            >
              {Array.from({ length: totalPages }, (_, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>

            <span>&nbsp;of {totalPages}</span>

            <button
              onClick={handleNextPage}
              disabled={currentPage === totalPages}
              className="pagination-btn"
            >
              Next âž¡
            </button>
          </div>
        )}
      </div>

      {/* ðŸ’… Styles */}
      <style>{`
        .shop-container {
          text-align: center;
          padding: 20px;
        }

        .shop-title {
          font-size: 2rem;
          font-weight: bold;
          margin-bottom: 30px;
        }

        .error-message {
          color: red;
          font-weight: bold;
          margin-top: 20px;
        }

        .product-list {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          gap: 25px;
        }

        .pagination-container {
          margin-top: 30px;
          display: flex;
          justify-content: center;
          align-items: center;
          gap: 10px;
        }

        .pagination-btn {
          background-color: #000;
          color: white;
          padding: 8px 15px;
          border: none;
          border-radius: 5px;
          cursor: pointer;
          font-weight: 600;
          transition: all 0.3s ease;
        }

        .pagination-btn:disabled {
          opacity: 0.5;
          cursor: not-allowed;
        }

        .pagination-btn:hover:not(:disabled) {
          background-color: #333;
        }

        select {
          padding: 5px 10px;
          border-radius: 5px;
          font-weight: 500;
        }
      `}</style>
    </>
  );
};

export default Shop;
