import React, { useState, useEffect } from "react";

const AutoSwipeCard = ({ images, interval = 3000 }) => {
  const [current, setCurrent] = useState(0);
  const length = images.length;

  useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrent((prev) => (prev === length - 1 ? 0 : prev + 1));
    }, interval);

    return () => clearInterval(slideInterval);
  }, [length, interval]);

  return (
    <div className="auto-swipe-card">
      <style>{`
        .auto-swipe-card {
          position: relative;
          width: 100%;
          max-width: 400px;
          height: 250px;
          overflow: hidden;
          border-radius: 12px;
          box-shadow: 0 4px 10px rgba(0,0,0,0.15);
          margin: 20px auto;
        }

        .auto-swipe-card img {
          width: 100%;
          height: 100%;
          object-fit: cover;
          position: absolute;
          top: 0;
          left: 0;
          opacity: 0;
          transition: opacity 0.8s ease;
        }

        .auto-swipe-card img.active {
          opacity: 1;
        }

        .dots-container {
          position: absolute;
          bottom: 10px;
          left: 50%;
          transform: translateX(-50%);
          display: flex;
          gap: 6px;
        }

        .dot {
          width: 10px;
          height: 10px;
          border-radius: 50%;
          background-color: rgba(255, 255, 255, 0.5);
          transition: background-color 0.3s ease;
        }

        .dot.active {
          background-color: #fff;
        }
      `}</style>

      {/* Images */}
      {images.map((img, index) => (
        <img
          key={index}
          src={img}
          alt={`slide-${index}`}
          className={index === current ? "active" : ""}
        />
      ))}

      {/* Dots */}
      <div className="dots-container">
        {images.map((_, index) => (
          <div
            key={index}
            className={`dot ${index === current ? "active" : ""}`}
            onClick={() => setCurrent(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default AutoSwipeCard;
