import React, { useState } from "react";

const ProductAdd = () => {
  const [addProduct, setAddProduct] = useState({
    id: "",
    productDisplayName: "",
    gender: "",
    articleType: "",
    baseColour: "",
    masterCategory: "",
    subCategory: "",
    season: "",
    year: "",
    usage: "",
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setAddProduct({
      ...addProduct,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await fetch("http://localhost:8000/add_product/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(addProduct),
      });

      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      const data = await response.json();
      console.log("‚úÖ Product added successfully:", data);

      // Reset form
      setAddProduct({
        id: "",
        productDisplayName: "",
        gender: "",
        articleType: "",
        baseColour: "",
        masterCategory: "",
        subCategory: "",
        season: "",
        year: "",
        usage: "",
      });
    } catch (err) {
      console.error("‚ùå Error adding product:", err);
      setError("Failed to add product. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
      <div className="bg-white shadow-2xl rounded-2xl p-8 w-full max-w-2xl border border-gray-100">
        <h2 className="text-3xl font-semibold text-center text-gray-800 mb-6">
          üõçÔ∏è Add New Product
        </h2>

        <form onSubmit={handleSubmit} className="space-y-5">
          {Object.keys(addProduct).map((key) => (
            <div key={key}>
              <label
                htmlFor={key}
                className="block text-gray-700 font-medium mb-2 capitalize"
              >
                {key.replace(/([A-Z])/g, " $1")}
              </label>
              <input
                type="text"
                id={key}
                name={key}
                value={addProduct[key]}
                onChange={handleChange}
                className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
                required
              />
            </div>
          ))}

          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 rounded-lg text-white font-semibold transition ${
              loading
                ? "bg-gray-400 cursor-not-allowed"
                : "bg-indigo-600 hover:bg-indigo-700"
            }`}
          >
            {loading ? "Adding..." : "Add Product"}
          </button>

          {error && (
            <div className="mt-4 bg-red-100 text-red-800 px-4 py-2 rounded-lg text-center">
              {error}
            </div>
          )}
        </form>
      </div>
    </div>
  );
};

export default ProductAdd;
