import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const UpdateProduct = () => {
  const { id: routeId } = useParams(); // /update_product/:id
  const [productId, setProductId] = useState(routeId || "");
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(false);
  const [updating, setUpdating] = useState(false);
  const [error, setError] = useState(null);

  // ‚úÖ Only auto-fetch if routeId exists (coming from delete page)
  useEffect(() => {
    if (routeId) {
      fetchProduct(routeId);
    }
  }, [routeId]);

  const fetchProduct = async (id) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`http://localhost:8000/products/${id}`);
      if (!response.ok) throw new Error("Product not found!");
      const data = await response.json();
      setProduct(data);
    } catch (err) {
      setError(err.message);
      setProduct(null);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setProduct({
      ...product,
      [e.target.name]: e.target.value,
    });
  };

  const handleFetchSubmit = (e) => {
    e.preventDefault();
    if (!productId.trim()) {
      setError("Please enter a valid product ID.");
      return;
    }
    fetchProduct(productId);
  };

    const handleUpdate = async (e) => {
    e.preventDefault();
    setUpdating(true);
    setError(null);

    try {
        // ‚úÖ Remove `id` field before sending
        const updatedProduct = { ...product };
        delete updatedProduct.id;

        const response = await fetch(`http://localhost:8000/update_product/${productId}/`, {
        method: "PUT",
        headers: {
            "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedProduct),
        });

        if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`HTTP ${response.status}: ${errorText}`);
        }

        const data = await response.json();
        alert("‚úÖ " + data.message);
    } catch (err) {
        console.error("Update error:", err);
        setError("‚ùå Failed to update product. Check backend console for more details.");
    } finally {
        setUpdating(false);
    }
    };


  return (
    <>
      <div className="update-container">
        <div className="card">
          <h2 className="title">üõçÔ∏è Update Product</h2>

          {/* Show ID input box only when product not loaded and no routeId */}
          {!routeId && !product && (
            <form onSubmit={handleFetchSubmit}>
              <div className="form-group">
                <label htmlFor="productId">Enter Product ID:</label>
                <input
                  type="text"
                  id="productId"
                  value={productId}
                  onChange={(e) => setProductId(e.target.value)}
                  required
                />
              </div>
              <button type="submit" disabled={loading}>
                {loading ? "Fetching..." : "Fetch Product"}
              </button>
            </form>
          )}

          {error && <div className="error-message">{error}</div>}

          {/* Once product is fetched, show update form */}
          {product && (
            <form onSubmit={handleUpdate}>
              {Object.entries(product)
                .filter(([key]) => key !== "id") // ‚úÖ ID not editable
                .map(([key, value]) => (
                  <div className="form-group" key={key}>
                    <label htmlFor={key}>{key}</label>
                    <input
                      type="text"
                      id={key}
                      name={key}
                      value={value || ""}
                      onChange={handleChange}
                    />
                  </div>
                ))}
              <button type="submit" disabled={updating}>
                {updating ? "Updating..." : "Update Product"}
              </button>
            </form>
          )}
        </div>
      </div>

      <style>
        {`
          .update-container {
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            background: linear-gradient(135deg, #eef2f7, #f9fafb);
            padding: 20px;
          }

          .card {
            background: #fff;
            padding: 40px;
            border-radius: 16px;
            box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
            width: 100%;
            max-width: 600px;
            transition: 0.3s ease;
          }

          .title {
            text-align: center;
            font-size: 28px;
            font-weight: 600;
            margin-bottom: 25px;
          }

          .form-group {
            display: flex;
            flex-direction: column;
            margin-bottom: 15px;
          }

          label {
            font-size: 14px;
            margin-bottom: 6px;
            font-weight: 500;
            text-transform: capitalize;
          }

          input {
            padding: 10px;
            border: 1px solid #ccc;
            border-radius: 8px;
            font-size: 15px;
          }

          input:focus {
            outline: none;
            border-color: #4f46e5;
            box-shadow: 0 0 0 2px rgba(79, 70, 229, 0.2);
          }

          button {
            background: #4f46e5;
            color: white;
            padding: 12px;
            border: none;
            border-radius: 8px;
            font-size: 16px;
            cursor: pointer;
            width: 100%;
            transition: background 0.3s ease;
          }

          button:hover {
            background: #4338ca;
          }

          button:disabled {
            background: #a5a5a5;
            cursor: not-allowed;
          }

          .error-message {
            background: #fee2e2;
            color: #991b1b;
            text-align: center;
            padding: 10px;
            border-radius: 8px;
            margin-top: 10px;
            font-weight: 500;
          }
        `}
      </style>
    </>
  );
};

export default UpdateProduct;
