import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const UpdatePage = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Product ID passed from delete page (if any)
  const passedId = location.state?.productId || "";

  const [productId, setProductId] = useState(passedId);
  const [product, setProduct] = useState(null);
  const [step, setStep] = useState(passedId ? "form" : "id"); // id -> ask for product id, form -> show form
  const [loading, setLoading] = useState(false);
  const [updating, setUpdating] = useState(false);
  const [message, setMessage] = useState("");

  // Fetch product details
  const fetchProduct = async (id) => {
    try {
      setLoading(true);
      setMessage("");
      const response = await fetch(`http://localhost:8000/get_product/${id}/`);
      if (!response.ok) throw new Error("Product not found");
      const data = await response.json();
      setProduct(data);
      setStep("form");
    } catch (error) {
      setMessage("‚ùå Unable to fetch product details. Please check the ID.");
      setProduct(null);
    } finally {
      setLoading(false);
    }
  };

  // Auto-fetch if navigated from delete page
  useEffect(() => {
    if (passedId) fetchProduct(passedId);
  }, [passedId]);

  // Handle input form submission (direct open case)
  const handleFetchById = (e) => {
    e.preventDefault();
    if (productId) fetchProduct(productId);
  };

  // Handle field updates
  const handleChange = (e) => {
    setProduct({
      ...product,
      [e.target.name]: e.target.value,
    });
  };

  // Handle update submission
  const handleUpdate = async (e) => {
    e.preventDefault();
    setUpdating(true);
    setMessage("");

    try {
      const updatedData = { ...product };
      delete updatedData.id; // Do not send id to backend
      delete updatedData.s3_image_url; // Ignore if present

      const response = await fetch(`http://localhost:8000/update_product/${productId}/`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedData),
      });

      if (!response.ok) throw new Error("Update failed");

      const result = await response.json();
      setMessage("‚úÖ " + result.message);
    } catch (error) {
      setMessage("‚ùå Failed to update product.");
    } finally {
      setUpdating(false);
    }
  };

  return (
    <div className="update-page">
      <div className="card">
        <h2>üõ†Ô∏è Update Product</h2>

        {/* Step 1: Ask for Product ID */}
        {step === "id" && (
          <form onSubmit={handleFetchById} className="id-form">
            <label htmlFor="productId">Enter Product ID:</label>
            <input
              type="number"
              id="productId"
              value={productId}
              onChange={(e) => setProductId(e.target.value)}
              placeholder="e.g. 101"
              required
            />
            <button type="submit" disabled={loading}>
              {loading ? "Fetching..." : "Fetch Product"}
            </button>
            {message && <div className="message error">{message}</div>}
          </form>
        )}

        {/* Step 2: Update Form */}
        {step === "form" && product && (
          <form onSubmit={handleUpdate} className="update-form">
            {Object.entries(product)
              .filter(([key]) => key !== "id" && key !== "s3_image_url")
              .map(([key, value]) => (
                <div className="form-group" key={key}>
                  <label htmlFor={key}>{key}</label>
                  <input
                    type="text"
                    id={key}
                    name={key}
                    value={value || ""}
                    onChange={handleChange}
                  />
                </div>
              ))}

            {message && (
              <div className={`message ${message.startsWith("‚úÖ") ? "success" : "error"}`}>
                {message}
              </div>
            )}

            <div className="buttons">
              <button type="submit" disabled={updating}>
                {updating ? "Updating..." : "Update Product"}
              </button>
              <button type="button" onClick={() => navigate("/delete_product")}>
                Go Back
              </button>
            </div>
          </form>
        )}
      </div>

      <style>
        {`
          .update-page {
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            background: linear-gradient(135deg, #f7f9fc, #edf2f7);
            padding: 20px;
          }

          .card {
            background: white;
            padding: 40px;
            border-radius: 16px;
            box-shadow: 0 10px 25px rgba(0,0,0,0.1);
            width: 100%;
            max-width: 600px;
          }

          h2 {
            text-align: center;
            margin-bottom: 25px;
            color: #333;
          }

          .id-form, .update-form {
            display: flex;
            flex-direction: column;
            gap: 15px;
          }

          label {
            font-weight: 500;
            color: #444;
          }

          input {
            padding: 10px;
            border-radius: 8px;
            border: 1px solid #ccc;
            font-size: 15px;
          }

          button {
            background: #2563eb;
            color: white;
            padding: 10px;
            border: none;
            border-radius: 8px;
            font-size: 16px;
            font-weight: 600;
            cursor: pointer;
          }

          button:hover {
            background: #1e40af;
          }

          .buttons {
            display: flex;
            justify-content: space-between;
            margin-top: 15px;
          }

          .message {
            margin-top: 10px;
            padding: 10px;
            border-radius: 8px;
            text-align: center;
            font-weight: 500;
          }

          .success {
            background: #dcfce7;
            color: #166534;
          }

          .error {
            background: #fee2e2;
            color: #991b1b;
          }
        `}
      </style>
    </div>
  );
};

export default UpdatePage;
