import React from "react";

const ProductCard = ({
  product_name,
  price,
  category,
  subcategory,
  product_images = [],
}) => {
  let images = [];

  // ðŸ§© Handle different formats of product_images
  if (typeof product_images === "string") {
    try {
      const jsonFixed = product_images.replace(/'/g, '"');
      const parsed = JSON.parse(jsonFixed);
      images = parsed.map((obj) => Object.keys(obj)[0]);
    } catch {
      images = product_images.match(/[\w-]+\.(jpg|png|jpeg|webp)/g) || [];
    }
  } else if (Array.isArray(product_images)) {
    images = product_images;
  }

  // ðŸ—ï¸ Build local image path â€” e.g. public/zara_images/Men/Subcategory/img.jpg
  const basePath = `${process.env.PUBLIC_URL}/zara_images/${category}/${subcategory}`;
  const imageSrc =
    images.length > 0 ? `${basePath}/${images[0]}` : `${process.env.PUBLIC_URL}/zara_images/no-image.jpg`;

  return (
    <div className="product-card-item">
      <a href="#" className="product-link">
        <div className="product-image-gallery">
          <img
            src={imageSrc}
            alt={product_name}
            className="product-image"
            loading="lazy"
            onError={(e) => {
              e.target.src = `${process.env.PUBLIC_URL}/zara_images/no-image.jpg`;
            }}
          />
        </div>

        <div className="product-info">
          <h3 className="product-name">{product_name}</h3>
          <p className="subcategory">{subcategory}</p>
          <p className="price">â‚¹{price}</p>
        </div>
      </a>
    </div>
  );
};

export default ProductCard;
