import React from "react";
import { useCart } from "../context/CartContext"; // ✅ import hook from context

export default function Tshirt() {
  const { addToCart } = useCart();

  const handleAdd = () => {
    addToCart({
      id: 1,
      name: "Classic T-shirt",
      price: 499,
      color: "Black",
      size: "M",
    });
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold">Classic T-shirt</h2>
      <p>₹499</p>
      <button
        onClick={handleAdd}
        className="mt-3 bg-blue-500 text-white px-4 py-2 rounded"
      >
        Add to Cart
      </button>
    </div>
  );
}
