import React from "react";
import { useCart } from "./context/CartContext";

export default function MyCart() {
  const { cart, removeFromCart, clearCart } = useCart();

  const subtotal = cart.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-6">üõí My Cart</h2>

      {cart.length === 0 ? (
        <div className="text-center py-16 border rounded-xl">
          <p className="text-lg mb-4">Your cart is empty</p>
          <span className="text-5xl">üõçÔ∏è</span>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">

          {/* CART ITEMS */}
          <div className="md:col-span-2 space-y-4">
            {cart.map((item) => (
              <div
                key={`${item.id}-${item.size}`}
                className="flex gap-4 p-4 border rounded-xl shadow-sm"
              >
                <img
                  src={item.image}
                  alt={item.name}
                  className="w-24 h-24 object-cover rounded-lg"
                />

                <div className="flex-1">
                  <h3 className="font-semibold text-lg">{item.name}</h3>
                  <p className="text-sm text-gray-500">
                    Size: {item.size}
                  </p>
                  <p className="text-sm text-gray-500">
                    ‚Çπ{item.price} √ó {item.quantity}
                  </p>
                </div>

                <div className="flex flex-col items-end justify-between">
                  <p className="font-semibold">
                    ‚Çπ{item.price * item.quantity}
                  </p>
                  <button
                    onClick={() => removeFromCart(item.id)}
                    className="text-sm text-red-600 hover:underline"
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* ORDER SUMMARY */}
          <div className="border rounded-xl p-6 h-fit shadow-sm">
            <h3 className="text-lg font-semibold mb-4">Order Summary</h3>

            <div className="flex justify-between mb-2">
              <span>Subtotal</span>
              <span>‚Çπ{subtotal}</span>
            </div>

            <div className="flex justify-between mb-2 text-gray-500">
              <span>Shipping</span>
              <span>Free</span>
            </div>

            <hr className="my-3" />

            <div className="flex justify-between font-semibold text-lg">
              <span>Total</span>
              <span>‚Çπ{subtotal}</span>
            </div>

            <button className="mt-6 w-full bg-black text-white py-3 rounded-xl hover:opacity-90">
              Proceed to Checkout
            </button>

            <button
              onClick={clearCart}
              className="mt-3 w-full text-sm text-gray-500 hover:underline"
            >
              Clear Cart
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
