import { Link } from "react-router-dom";

const products = [
  {
    id: 1,
    name: "Menâ€™s Classic Cotton T-Shirt",
    price: 599,
    img: "/images/Men Grey Tshirt.jpg",
  },
  {
    id: 2,
    name: "Menâ€™s Polo T-Shirt",
    price: 799,
    img: "/images/Men Red Tshirt.jpg",
  },
  {
    id: 3,
    name: "Menâ€™s Oversized Tee",
    price: 899,
    img: "/images/Men Levendar Tshirt.jpg",
  },
];

const MenTopwear = () => {
  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Menâ€™s Topwear</h2>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-6">
        {products.map((p) => (
          <Link
            key={p.id}
            to={`/tshirt/${p.id}`} // ðŸ‘ˆ navigate to detail page
            className="border rounded-lg hover:shadow-md transition"
          >
            <img
              src={p.img}
              alt={p.name}
              className="w-full h-64 object-cover rounded-t-lg"
            />
            <div className="p-3">
              <h4 className="font-semibold text-lg">{p.name}</h4>
              <p className="text-gray-600">â‚¹{p.price}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default MenTopwear;
