import React from "react";

const ProductCard = ({
  product_name,
  price,
  category,
  subcategory,
  details,
  product_images = [],
}) => {
  const safeProductName = product_name?.replace(/[^a-zA-Z0-9 _-]/g, "").trim();

  // ðŸ§  Fix: Ensure product_images is always an array
  let images = [];
  if (typeof product_images === "string") {
    try {
      // Replace single quotes â†’ double quotes, make it JSON safe
      const jsonFixed = product_images
        .replace(/'/g, '"')
        .replace(/(\w+):/g, '"$1":'); // ensure proper JSON keys
      const parsed = JSON.parse(jsonFixed);
      // Extract only the URLs from the parsed list of objects
      images = parsed.map((obj) => Object.keys(obj)[0]);
    } catch (e) {
      // Fallback: regex to pull all image URLs
      images = product_images.match(/https?:\/\/[^\s'"]+/g) || [];
    }
  } else if (Array.isArray(product_images)) {
    images = product_images;
  }

  return (
    <div className="product-card-item">
      <a target="_blank" rel="noopener noreferrer" className="product-link">
        <div className="product-image-gallery">
          {images && images.length > 0 ? (
            images.map((img, idx) => {
              const localPath = `/zara_images/${category}/${safeProductName}/${img
                .split("/")
                .pop()
                .split("?")[0]}`;
              return (
                <img
                  key={idx}
                  src={localPath}
                  alt={`${product_name} ${idx + 1}`}
                  className="product-image"
                  loading="lazy"
                />
              );
            })
          ) : (
            <div className="no-image">No image available</div>
          )}
        </div>

        <div className="product-info">
          <h3 className="product-name">{product_name}</h3>
          <p className="subcategory">{subcategory}</p>
          <p className="category">{category}</p>
          <p className="price">{price}</p>
        </div>
      </a>
    </div>
  );
};

export default ProductCard;
