import React, { Component } from "react";
import top_products from "../Data/top_products";
import popular_products from "../Data/popular_products";

// Example ProductCard component
const ProductCard = ({ displayname, type, image }) => (
  <div className="product-card-item">
    <img src={`/Data/Images/${image}`}  alt="Product" />
    <h3>{displayname}</h3>
    <p>₹{type}</p>
  </div>
);


export default class Home extends Component {
  render() {
    return (
      <>
        <style>{`
          /* Top Banner */
          .top-banner {
            display: flex;
            justify-content: space-between;
            align-items: center;
            background: linear-gradient(90deg, #FF6F61, #FF3D00); 
            padding: 20px 40px;
            border-radius: 12px;
            color: #fff;
            width: 100%;
            box-sizing: border-box;
            margin-bottom: 30px;
          }

          .top-banner-content {
            flex: 1;
          }

          .top-banner-content h1 {
            font-size: 2rem;
            margin: 0 0 10px;
            font-weight: bold;
          }

          .top-banner-content p {
            font-size: 1.1rem;
            margin: 5px 0;
          }

          .Limited_Time {
            margin-top: 10px;
            font-size: 0.9rem;
            font-style: italic;
            color: #ffe6e0;
          }

          /* Shop Now Button */
          .shop-now-btn {
            display: inline-block;
            margin-top: 15px;
            padding: 12px 25px;
            background-color: #fff;
            color: #FF3D00;
            font-weight: bold;
            border-radius: 8px;
            text-decoration: none;
            cursor: pointer;
            transition: all 0.3s ease;
          }

          .shop-now-btn:hover {
            background-color: #ffe6e0;
            color: #FF6F61;
            transform: translateY(-2px);
          }

          .top-banner img {
            width: 200px;
            height: 200px;
            object-fit: cover;
            border-radius: 12px;
            margin-left: 20px;
          }

          @media (max-width: 768px) {
            .top-banner {
              flex-direction: column;
              text-align: center;
            }

            .top-banner img {
              margin-left: 0;
              margin-top: 15px;
              width: 150px;
              height: 150px;
            }

            .Limited_Time {
              font-size: 0.7rem;
              margin-top: 8px;
            }

            .shop-now-btn {
              margin-top: 12px;
            }
          }

          /* Top Products Section */
          .product-card {
            padding: 20px 40px;
          }

          .product-card h1 {
            margin-bottom: 20px;
            font-size: 1.8rem;
          }

          .product-list {
            display: flex;
            flex-wrap: wrap;
            gap: 20px;
          }

          .product-card-item {
            flex: 1 1 calc(25% - 20px); /* 4 cards per row */
            background-color: #fff;
            border-radius: 12px;
            box-shadow: 0 2px 8px rgba(0,0,0,0.1);
            padding: 15px;
            text-align: center;
            transition: transform 0.2s ease;
          }

          .product-card-item:hover {
            transform: translateY(-5px);
          }

          .product-card-item img {
            width: 100%;
            height: 180px;
            object-fit: cover;
            border-radius: 10px;
            margin-bottom: 10px;
          }

          .product-card-item h3 {
            font-size: 1rem;
            margin: 0 0 5px;
          }

          .product-card-item p {
            font-size: 0.9rem;
            color: #555;
            margin: 0;
          }

          @media (max-width: 768px) {
            .product-card-item {
              flex: 1 1 calc(50% - 20px); /* 2 cards per row */
            }
          }

          @media (max-width: 480px) {
            .product-card-item {
              flex: 1 1 100%; /* full width on small screens */
            }
          }

        .Newsletter-card {
            display: flex;
            justify-content: space-between;
            align-items: center;
            background: linear-gradient(90deg, #6f6d6dff, #4f4d4cff); 
            padding: 10px 40px;
            border-radius: 12px;
            color: #fff;
            width: 100%;
            box-sizing: border-box;
            margin-bottom: 30px;
        }

        .Newsletter-card input {
            flex: 1;
            padding: 10px;
            border: none;
            border-radius: 5px;
            margin-right: 10px;
            font-size: 16px;
        }

        .Newsletter-card button {
            padding: 10px 20px;
            background-color: #0d0c0cff;
            color: #fafafaff;
            border: none;
            border-radius: 5px;
            font-size: 16px;
            cursor: pointer;
            transition: background-color 0.3s ease;
            font-style: bold;
        }

        .Newsletter-card button:hover {
            background-color: #ffe6e0;
            color: #FF6F61;
        }

        .Newsletter-card h1 {
            font-size: 1.5rem;
            margin: 0;
            font-weight: bold;
            flex: 1;
            align-self: center;
            font-family: 'Arial', sans-serif;
        }
        `}
        </style>

        {/* Top Banner */}
        <div className="top-banner">
          <div className="top-banner-content">
            <h1>Biggest Diwali Sale</h1>
            <p>Get up to 80% off on your favourite Attire.</p>
            <div className="Limited_Time">*Limited time offer</div>
            <a href="#top-products" className="shop-now-btn">Shop Now</a>
          </div>
          <img src="/Data/Images/BiggestDiwaliSale.png" alt="Biggest Diwali Sale" />
        </div>

        {/* Top Products Section */}
        <div className="product-card" id="top-products">
          <h1>Top Products</h1>
          <div className="product-list">
            {top_products.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))}
          </div>
        </div>

        {/* Newsletter Subscription */}
        <div className="Newsletter-card" >
          <h1>Subscribe for latest update on fashion.</h1>
          <input type="text" placeholder="Enter your email" />
          <button>Subscribe</button>
        </div>

        {/* Popular Products Section */}
        <div className="product-card" id="popular-products">
          <h1>Popular Products</h1>
          <div className="product-list">
            {popular_products.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))}
          </div>
        </div>
      </>
    );
  }
}
