import React, { useContext, useState, useMemo } from "react";
import { ShopContext } from "../context/Shopcontext";
import ProductCard from "./Product Card";

const Shop = (props) => {
  const { cleaned_zara_all_data } = useContext(ShopContext);

  const [sortBy, setSortBy] = useState("Featured");
  const [itemsPerPage, setItemsPerPage] = useState(16);
  const [viewType, setViewType] = useState("Grid");
  const [filter, setFilter] = useState("Subcategory");
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [showFilterPanel, setShowFilterPanel] = useState(false);

  // 🧩 Filter by category
  const filteredByCategory = cleaned_zara_all_data.filter(
    (item) => item.category === props.category
  );

  // 🧩 Unique subcategories
  const subcategories = [...new Set(filteredByCategory.map((i) => i.subcategory))];
  const prices = ["Below ₹1000", "₹1000 - ₹3000", "Above ₹3000"];

  // 🧠 Handle filter toggling
  const handleFilterChange = (value) => {
    setSelectedFilters((prev) =>
      prev.includes(value) ? prev.filter((f) => f !== value) : [...prev, value]
    );
  };

  // 🧩 Apply selected filters
  const filteredData = useMemo(() => {
    let data = [...filteredByCategory];

    if (filter === "Subcategory" && selectedFilters.length > 0) {
      data = data.filter((item) => selectedFilters.includes(item.subcategory));
    } else if (filter === "Price" && selectedFilters.length > 0) {
      data = data.filter((item) => {
        const price = parseFloat(item.price?.replace(/[^\d]/g, "")) || 0;
        return selectedFilters.some((f) => {
          if (f === "Below ₹1000") return price < 1000;
          if (f === "₹1000 - ₹3000") return price >= 1000 && price <= 3000;
          if (f === "Above ₹3000") return price > 3000;
          return false;
        });
      });
    }

    return data;
  }, [filteredByCategory, filter, selectedFilters]);

  // 🧩 Sort products
  const sortedData = useMemo(() => {
    let sorted = [...filteredData];
    if (sortBy === "Price")
      sorted.sort(
        (a, b) =>
          (parseFloat(a.price?.replace(/[^\d]/g, "")) || 0) -
          (parseFloat(b.price?.replace(/[^\d]/g, "")) || 0)
      );
    if (sortBy === "Name")
      sorted.sort((a, b) => a.product_name.localeCompare(b.product_name));
    return sorted;
  }, [filteredData, sortBy]);

  // 🧩 Paginate data
  const paginatedData = sortedData.slice(0, itemsPerPage);

  // 🖼️ Build correct local image paths
  const dataWithLocalImages = useMemo(() => {
    return paginatedData.map((item) => {
      let images = [];

      if (item.product_images) {
        try {
          let parsed = item.product_images;

          if (typeof parsed === "string") {
            parsed = JSON.parse(parsed.replace(/'/g, '"'));
          }

          const category = item.category?.trim() || "Men";
          const subcategory = item.subcategory
            ?.trim()
            .replace(/\s+/g, "_")
            .replace(/[^\w-]/g, "");

          images = parsed.map((img) => {
            const filename =
              typeof img === "string" ? img : Object.keys(img)[0];
            return `/zara_images/${category}/${subcategory}/${filename}`;
          });
        } catch (err) {
          console.error("Error parsing product_images for", item.product_name, err);
        }
      }

      return { ...item, product_images: images };
    });
  }, [paginatedData]);

  return (
    <div className="shop-container">
      <style>{`
        .shop-container {
          font-family: "Poppins", sans-serif;
          padding: 20px;
        }
        .banner-section {
          display: flex;
          justify-content: space-between;
          align-items: center;
          background-image: linear-gradient(to right, rgba(245, 233, 221, 0.85), rgba(222, 220, 220, 0.7)),
            url("/Data/Images/Diwali.jpg");
          border-radius: 12px;
          box-shadow: 0 4px 10px rgba(0,0,0,0.1);
          padding: 30px 50px;
          margin: 20px auto 40px;
          max-width: 1200px;
          flex-wrap: wrap;
        }
        .banner-text {
          flex: 1;
          text-align: left;
          padding-right: 30px;
          min-width: 300px;
        }
        .banner-title {
          font-size: 2rem;
          font-weight: 700;
          color: #222;
          margin-bottom: 10px;
        }
        .banner-subtitle {
          font-size: 1rem;
          color: #444;
          margin-bottom: 20px;
        }
        .banner-button {
          background-color: #ff6f61;
          color: #fff;
          border: none;
          padding: 10px 20px;
          border-radius: 25px;
          font-size: 0.95rem;
          cursor: pointer;
          font-weight: 600;
          transition: all 0.3s ease;
        }
        .banner-button:hover { background-color: #e65550; }
        .banner-image {
          width: 280px;
          height: 220px;
          object-fit: cover;
          border-radius: 12px;
          box-shadow: 0 4px 10px rgba(0,0,0,0.2);
        }
        .shop-content {
          display: flex;
          width: 100%;
          max-width: 1200px;
          gap: 20px;
          margin: 0 auto;
        }
        .filter-sidebar {
          width: 220px;
          background: #fff;
          border-radius: 10px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
          padding: 20px;
          flex-shrink: 0;
          height: fit-content;
        }
        .filter-option { margin-bottom: 10px; text-align: left; }
        .filter-option label { display: flex; align-items: center; gap: 8px; }
        .apply-btn {
          background-color: #ff6f61;
          color: #fff;
          border: none;
          padding: 8px 18px;
          border-radius: 20px;
          cursor: pointer;
          font-weight: 600;
          margin-top: 10px;
          width: 100%;
        }
        .apply-btn:hover { background-color: #e65550; }
        .product-area { flex: 1; }
        .shop-header {
          display: flex;
          flex-wrap: wrap;
          justify-content: flex-start;
          align-items: center;
          gap: 10px;
          margin-bottom: 15px;
        }
        .select-box { padding: 5px; border-radius: 5px; border: 1px solid #ccc; }
        .product-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: 15px;
        }
        .product-card-item {
          background: linear-gradient(to right, rgba(245, 233, 221, 0.85), rgba(222, 220, 220, 0.7));
          border-radius: 10px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
          padding: 10px;
          text-align: center;
          transition: transform 0.2s ease;
        }
        .product-card-item:hover { transform: translateY(-4px); }
        .product-card-item img {
          width: 100%;
          height: 160px;
          object-fit: cover;
          border-radius: 8px;
          margin-bottom: 8px;
        }
        .product-card-item h3 { font-size: 0.95rem; margin: 5px 0; }
        .product-card-item p { font-size: 0.85rem; color: #555; margin: 0; }
        @media (max-width: 900px) {
          .shop-content { flex-direction: column; align-items: center; }
          .filter-sidebar { width: 90%; margin-bottom: 15px; }
          .banner-section { flex-direction: column; text-align: center; padding: 20px; }
          .banner-image { width: 90%; height: auto; margin-top: 15px; }
        }
      `}</style>

      {/* 🖼️ Banner */}
      <div className="banner-section">
        <div className="banner-text">
          <h1 className="banner-title">
            {props.category === "Men"
              ? "Men’s Collection"
              : props.category === "Woman"
              ? "Woman’s Collection"
              : "Kids’ Collection"}
          </h1>
          <p className="banner-subtitle">
            {props.category === "Men"
              ? "Aesthetic Men’s Wear for Every Occasion"
              : props.category === "Woman"
              ? "Chic, Elegant & Trendy Styles for You"
              : "Cute, Comfy & Colorful Fashion for Little Stars"}
          </p>
          <button className="banner-button">Shop Now →</button>
        </div>
        <img
          src={
            props.category === "Men"
              ? process.env.PUBLIC_URL + "/Data/Images/Men Banner.jpg"
              : props.category === "Woman"
              ? process.env.PUBLIC_URL + "/Data/Images/Women Banner.jpg"
              : process.env.PUBLIC_URL + "/Data/Images/Kids Banner.jpg"
          }
          alt={`${props.category} banner`}
          className="banner-image"
        />
      </div>

      {/* 🛍️ Main Content */}
      <div className="shop-content">
        {/* Filter Sidebar */}
        {showFilterPanel && (
          <div className="filter-sidebar">
            <h3>{filter} Filter</h3>
            {(filter === "Subcategory" ? subcategories : prices).map((value) => (
              <div key={value} className="filter-option">
                <label>
                  <input
                    type="checkbox"
                    checked={selectedFilters.includes(value)}
                    onChange={() => handleFilterChange(value)}
                  />
                  {value}
                </label>
              </div>
            ))}
            <button className="apply-btn" onClick={() => setShowFilterPanel(false)}>
              Apply Filter
            </button>
          </div>
        )}

        {/* Product Area */}
        <div className="product-area">
          <div className="shop-header">
            <span>
              Showing 1 - {Math.min(itemsPerPage, sortedData.length)} of {sortedData.length} Products
            </span>

            <label>
              Sort by{" "}
              <select className="select-box" value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
                <option>Featured</option>
                <option>Price</option>
                <option>Name</option>
              </select>
            </label>

            <label>
              Show{" "}
              <select
                className="select-box"
                value={itemsPerPage}
                onChange={(e) => setItemsPerPage(Number(e.target.value))}
              >
                <option value={16}>16</option>
                <option value={20}>20</option>
                <option value={24}>24</option>
              </select>
            </label>

            <label>
              View{" "}
              <select className="select-box" value={viewType} onChange={(e) => setViewType(e.target.value)}>
                <option>Grid</option>
                <option>List</option>
              </select>
            </label>

            <label>
              Filter{" "}
              <select
                className="select-box"
                value={filter}
                onChange={(e) => {
                  setFilter(e.target.value);
                  setShowFilterPanel(true);
                  setSelectedFilters([]);
                }}
              >
                <option>Subcategory</option>
                <option>Price</option>
              </select>
            </label>
          </div>

          <div className={viewType === "Grid" ? "product-grid" : "product-list"}>
            {dataWithLocalImages.map((item) => (
              <div className="product-card-item" key={item.id}>
                <ProductCard {...item} />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Shop;
