import React, {useContext } from 'react'
import { useParams } from 'react-router-dom'
import { ShopContext } from '../context/Shopcontext';
import {productDisplay} from './product';

const Item = () => {

  const { cleaned_zara_all_data } = useContext(ShopContext);
  const {productId} = useParams();
  const product = cleaned_zara_all_data.find((p) => p.id === Number(productId));

  if (!product) {
    return <div>Product not found</div>;
  }
  console.log(product.product_name);

    let images = [];
  const { product_images } = product;

  if (typeof product_images === "string") {
    try {
      const jsonFixed = product_images.replace(/'/g, '"');
      const parsed = JSON.parse(jsonFixed);
      images = parsed.map((obj) => Object.keys(obj)[0]);
    } catch {
      images = product_images.match(/https?:\/\/[^\s'"]+/g) || [];
    }
  } else if (Array.isArray(product_images)) {
    images = product_images;
  }
  return (
    <div className="product-page">
      <h1>{product.product_name}</h1>

      {images.length > 0 ? (
        <img
          src={images[0]}
          alt={product.product_name}
          className="product-image"
          loading="lazy"
        />
      ) : (
        <div className="no-image">No image</div>
      )}
      <product product={productDisplay}  />
    </div>

  );
};

export default Item;
