import React from "react";
import { Link } from "react-router-dom";

const ProductCard = (props) => {
  // ðŸ›¡ï¸ Safe image resolution
  const previewImage =
    Array.isArray(props.images) && props.images.length > 0
      ? props.images[0]
      : props.s3_image_url;

  return (
    <div className="product-card-item">
      <Link to={`/products/${props.id}/`} className="product-link">
        <div className="product-image-gallery">
          <img
            src={previewImage}
            alt={props.product_name}
            className="product-image"
            loading="lazy"
            onError={(e) => {
              e.target.src = "/placeholder.jpg"; // optional fallback
            }}
          />
        </div>

        <div className="product-info">
          <h3 className="product-name">{props.product_name}</h3>
          <p className="gender">{props.gender}</p>
          <p className="price">${(props.price / 1000).toFixed(1)}k</p>
          <p className="details">{props.details}</p>
        </div>
      </Link>
    </div>
  );
};

export default ProductCard;
