import React, { useContext } from "react";
import { useParams } from "react-router-dom";
import { ShopContext } from "../context/Shopcontext";
import ProductDisplay from './product';

const Item = () => {
  const { cleaned_zara_all_data } = useContext(ShopContext);
  const { id } = useParams();

  const select_product = cleaned_zara_all_data.find(
    (p) => p.id === Number(id)
  );

  if (!select_product) {
    return <div>Product not found</div>;
  }

  console.log(select_product.product_name);

  let images = [];
  const { product_images } = select_product;

  if (typeof product_images === "string") {
    try {
      const jsonFixed = product_images.replace(/'/g, '"');
      const parsed = JSON.parse(jsonFixed);
      images = parsed.map((obj) => Object.keys(obj)[0]);
    } catch {
      images = product_images.match(/https?:\/\/[^\s'"]+/g) || [];
    }
  } else if (Array.isArray(product_images)) {
    images = product_images;
  }

  return (
    <div className="product-page">
      <ProductDisplay product={select_product} />
    </div>
  );
};

export default Item;
