import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const UpdateProduct = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const preloadedId = location.state?.productId || "";
  const [productId, setProductId] = useState(preloadedId);
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(false);
  const [updating, setUpdating] = useState(false);
  const [error, setError] = useState("");
  const [step, setStep] = useState(preloadedId ? "form" : "id"); // id ‚Üí ask for ID, form ‚Üí show form

  // üîπ Fetch product details
  const fetchProduct = async (id) => {
    if (!id) return;
    setLoading(true);
    setError("");
    try {
      const response = await fetch(`http://localhost:8000/get_product/${id}/`);
      if (!response.ok) throw new Error(`HTTP ${response.status}`);
      const data = await response.json();
      setProduct(data);
      setStep("form");
    } catch (err) {
      console.error("‚ùå Error fetching product:", err);
      setError("Product not found or failed to fetch.");
    } finally {
      setLoading(false);
    }
  };

  // üîπ If navigated from delete page, fetch immediately
  useEffect(() => {
    if (preloadedId) fetchProduct(preloadedId);
  }, [preloadedId]);

  // üîπ Handle user-entered ID (direct access)
  const handleFetchById = (e) => {
    e.preventDefault();
    if (productId) fetchProduct(productId);
  };

  const handleChange = (e) => {
    setProduct({
      ...product,
      [e.target.name]: e.target.value,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!productId) return;

    setUpdating(true);
    setError("");

    try {
      const updatedData = { ...product };
      delete updatedData.id; // remove id from body if present

      const response = await fetch(`http://localhost:8000/update_product/${productId}/`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedData),
      });

      if (!response.ok) {
        const text = await response.text();
        throw new Error(text);
      }

      const data = await response.json();
      alert(`‚úÖ ${data.message}`);
      navigate("/delete_product"); // optional redirect after update
    } catch (err) {
      console.error("‚ùå Update error:", err);
      setError("Failed to update product. Please check your inputs.");
    } finally {
      setUpdating(false);
    }
  };

  return (
    <div className="update-container">
      <h2>üõ†Ô∏è Update Product</h2>

      {/* Step 1: Ask for Product ID */}
      {step === "id" && (
        <form onSubmit={handleFetchById} className="id-form">
          <label htmlFor="productId">Enter Product ID:</label>
          <input
            type="number"
            id="productId"
            value={productId}
            onChange={(e) => setProductId(e.target.value)}
            placeholder="e.g. 1234"
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? "Fetching..." : "Fetch Product"}
          </button>
          {error && <div className="error-msg">{error}</div>}
        </form>
      )}

      {/* Step 2: Show Update Form */}
      {step === "form" && product && (
        <form onSubmit={handleUpdate} className="update-form">
          {Object.entries(product)
            .filter(([key]) => key !== "id" && key !== "s3_image_url")
            .map(([key, value]) => (
              <div key={key} className="form-group">
                <label htmlFor={key}>{key}</label>
                <input
                  type="text"
                  id={key}
                  name={key}
                  value={value || ""}
                  onChange={handleChange}
                />
              </div>
            ))}

          {error && <div className="error-msg">{error}</div>}
          <button type="submit" disabled={updating}>
            {updating ? "Updating..." : "Update Product"}
          </button>
        </form>
      )}

      <style>{`
        .update-container {
          max-width: 650px;
          margin: 50px auto;
          padding: 30px;
          background: #fff;
          border-radius: 12px;
          box-shadow: 0 10px 25px rgba(0,0,0,0.1);
        }
        h2 {
          text-align: center;
          margin-bottom: 25px;
          color: #333;
        }
        .id-form, .update-form {
          display: flex;
          flex-direction: column;
          gap: 15px;
        }
        label {
          font-weight: 600;
          text-transform: capitalize;
          color: #444;
        }
        input {
          padding: 10px;
          border: 1px solid #ccc;
          border-radius: 8px;
          font-size: 15px;
        }
        button {
          background: #2563eb;
          color: #fff;
          padding: 12px;
          border: none;
          border-radius: 8px;
          cursor: pointer;
          font-size: 16px;
          font-weight: 600;
        }
        button:hover {
          background: #1e40af;
        }
        .error-msg {
          color: #b91c1c;
          background: #fee2e2;
          padding: 8px;
          border-radius: 6px;
          font-weight: 500;
          text-align: center;
        }
      `}</style>
    </div>
  );
};

export default UpdateProduct;
