import React from 'react'
import { useParams } from 'react-router-dom'
import cleaned_zara_all_data from '../Data/cleaned_zara_all_data';

const Item = () => {
  const {productID} = useParams();

  const product = cleaned_zara_all_data.find((p) => p.id?.toString() === productID);

  if (!product) {
    return <div>Product not found</div>;
  }
  console.log(product.id);

  let images = [];
  const { product_images } = product.product_images;
  if (typeof product_images === "string") {
    try {
      const jsonFixed = product_images.replace(/'/g, '"');
      const parsed = JSON.parse(jsonFixed);
      images = parsed.map((obj) => Object.keys(obj)[0]);
    } catch {
      images = product_images.match(/https?:\/\/[^\s'"]+/g) || [];
    }
  } else if (Array.isArray(product_images)) {
    images = product_images;
  }

  return (
    <div className="product-page">
      <h1>{product.product_name}</h1>
        {images.length > 0 ? (
          <img src={images[0]}
              alt={product.product_name}
              className="product-image"
              loading="lazy" />
              ) : (
            <div className="no-image">No image</div>
          )}
      <p>Price: â‚¹{product.price}</p>
      <p>Category: {product.category}</p>
      <p>Subcategory: {product.subcategory}</p>
    </div>
  );
};

export default Item;
