import React, { useContext, useState, useMemo } from "react";
import { ShopContext } from "../context/Shopcontext";
import ProductCard from "./Product Card";

const Shop = (props) => {
  const { cleaned_zara_all_data } = useContext(ShopContext);

  const [sortBy, setSortBy] = useState("Featured");
  const [itemsPerPage, setItemsPerPage] = useState(16);
  const [viewType, setViewType] = useState("Grid");
  const [filter, setFilter] = useState("Subcategory");
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [showFilterPanel, setShowFilterPanel] = useState(false);

  const bannerImages = {
    Men: process.env.PUBLIC_URL + "/Data/Images/Men Banner.jpg",
    Woman: process.env.PUBLIC_URL + "/Data/Images/Women Banner.jpg",
    Kids: process.env.PUBLIC_URL + "/Data/Images/Kids Banner.jpg",
  };

  const categoryText = {
    Men: {
      title: "Men’s Collection",
      subtitle: "Aesthetic Men’s Wear for Every Occasion",
    },
    Woman: {
      title: "Woman’s Collection",
      subtitle: "Chic, Elegant & Trendy Styles for You",
    },
    Kids: {
      title: "Kids’ Collection",
      subtitle: "Cute, Comfy & Colorful Fashion for Little Stars",
    },
  };

  // Filter base data by category
  const filteredByCategory = cleaned_zara_all_data.filter(
    (item) => item.category === props.category
  );

  // Dynamic filter options
  const subcategories = [...new Set(filteredByCategory.map((i) => i.subcategory))];
  const prices = ["Below ₹1000", "₹1000 - ₹3000", "Above ₹3000"];

  // Handle filter selection toggle
  const handleFilterChange = (value) => {
    setSelectedFilters((prev) =>
      prev.includes(value)
        ? prev.filter((f) => f !== value)
        : [...prev, value]
    );
  };

  // Apply filter logic
  const filteredData = useMemo(() => {
    let data = [...filteredByCategory];
    if (filter === "Subcategory" && selectedFilters.length > 0) {
      data = data.filter((item) => selectedFilters.includes(item.subcategory));
    } else if (filter === "Price" && selectedFilters.length > 0) {
      data = data.filter((item) => {
        const price = item.price || 0;
        return selectedFilters.some((f) => {
          if (f === "Below ₹1000") return price < 1000;
          if (f === "₹1000 - ₹3000") return price >= 1000 && price <= 3000;
          if (f === "Above ₹3000") return price > 3000;
          return false;
        });
      });
    }
    return data;
  }, [filteredByCategory, filter, selectedFilters]);

  // Sorting
  const sortedData = useMemo(() => {
    let sorted = [...filteredData];
    if (sortBy === "Price") sorted.sort((a, b) => (a.price || 0) - (b.price || 0));
    if (sortBy === "Name") sorted.sort((a, b) => a.product_name.localeCompare(b.product_name));
    return sorted;
  }, [filteredData, sortBy]);

  const paginatedData = sortedData.slice(0, itemsPerPage);

  return (
    <div className="shop-container">
      <style>{`
        .shop-container {
          text-align: center;
          font-family: "Poppins", sans-serif;
          padding-bottom: 40px;
          overflow-x: hidden;
          display: flex;
          justify-content: center;
        }

        /* 🧱 Layout: Sidebar + Content */
        .shop-content {
          display: flex;
          width: 100%;
          max-width: 1200px;
        }

        .filter-sidebar {
          width: 250px;
          background: #fff;
          border-radius: 10px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
          padding: 20px;
          margin-right: 20px;
          height: fit-content;
        }

        .filter-option {
          text-align: left;
          margin-bottom: 10px;
        }

        .filter-option label {
          display: flex;
          align-items: center;
          gap: 8px;
        }

        .apply-btn {
          background-color: #ff6f61;
          color: #fff;
          border: none;
          padding: 8px 18px;
          border-radius: 20px;
          cursor: pointer;
          font-weight: 600;
          margin-top: 10px;
          width: 100%;
        }

        .apply-btn:hover {
          background-color: #e65550;
        }

        /* 🔳 Grid */
        .product-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
          gap: 18px;
          flex: 1;
        }

        /* 📋 List */
        .product-list {
          display: flex;
          flex-direction: column;
          gap: 15px;
          flex: 1;
        }

        .product-card-item {
          background: linear-gradient(to right, rgba(245, 233, 221, 0.85), rgba(222, 220, 220, 0.7));
          border-radius: 10px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
          padding: 10px;
          text-align: center;
        }

        @media (max-width: 900px) {
          .shop-content {
            flex-direction: column;
            align-items: center;
          }
          .filter-sidebar {
            width: 90%;
            margin-bottom: 20px;
          }
        }
      `}</style>

      <div className="shop-content">
        {/* 🧩 Left Filter Sidebar */}
        {showFilterPanel && (
          <div className="filter-sidebar">
            <h3>{filter} Filter</h3>
            {(filter === "Subcategory" ? subcategories : prices).map((value) => (
              <div key={value} className="filter-option">
                <label>
                  <input
                    type="checkbox"
                    checked={selectedFilters.includes(value)}
                    onChange={() => handleFilterChange(value)}
                  />
                  {value}
                </label>
              </div>
            ))}
            <button
              className="apply-btn"
              onClick={() => setShowFilterPanel(false)}
            >
              Apply Filter
            </button>
          </div>
        )}

        {/* 🛍️ Product Area */}
        <div className="product-area">
          {/* Controls */}
          <div className="shop-header">
            <span>
              Showing 1 - {Math.min(itemsPerPage, sortedData.length)} of{" "}
              {sortedData.length} Products
            </span>

            <label>
              Sort by{" "}
              <select
                className="select-box"
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
              >
                <option>Featured</option>
                <option>Price</option>
                <option>Name</option>
              </select>
            </label>

            <label>
              Show{" "}
              <select
                className="select-box"
                value={itemsPerPage}
                onChange={(e) => setItemsPerPage(Number(e.target.value))}
              >
                <option value={16}>16</option>
                <option value={20}>20</option>
                <option value={24}>24</option>
              </select>
            </label>

            <label>
              View{" "}
              <select
                className="select-box"
                value={viewType}
                onChange={(e) => setViewType(e.target.value)}
              >
                <option>Grid</option>
                <option>List</option>
              </select>
            </label>

            <label>
              Filter{" "}
              <select
                className="select-box"
                value={filter}
                onChange={(e) => {
                  setFilter(e.target.value);
                  setShowFilterPanel(true);
                  setSelectedFilters([]);
                }}
              >
                <option>Subcategory</option>
                <option>Price</option>
              </select>
            </label>
          </div>

          {/* Product Grid/List */}
          <div className={viewType === "Grid" ? "product-grid" : "product-list"}>
            {paginatedData.map((item) => (
              <div className="product-card-item" key={item.id}>
                <ProductCard {...item} />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Shop;
