import React, { useState, useMemo, useEffect, useRef } from "react";
import ProductCard from "./Product Card";

const Shop = ({ category }) => {
  const [products, setProducts] = useState([]);
  const [sortBy, setSortBy] = useState("Featured");
  const [itemsPerPage, setItemsPerPage] = useState(16);
  const [currentPage, setCurrentPage] = useState(1);
  const [filterPanelOpen, setFilterPanelOpen] = useState(false);
  const [selectedFilters, setSelectedFilters] = useState({ subCategory: [] });

  const filterPanelRef = useRef(null);

  // ✅ Fetch data from FastAPI
  useEffect(() => {
    fetch("http://localhost:8000/products/")
      .then((res) => {
        if (!res.ok) throw new Error(`HTTP error: ${res.status}`);
        return res.json();
      })
      .then((data) => {
        console.log("✅ Products fetched:", data);
        setProducts(data);
      })
      .catch((err) => {
        console.error("❌ Error fetching products:", err);
      });
  }, []);

  // ✅ Filter data
  const filteredData = useMemo(() => {
    let data = [...products];
    if (category) data = data.filter((p) => p.category === category);
    if (selectedFilters.subCategory.length > 0) {
      data = data.filter((p) =>
        selectedFilters.subCategory.includes(p.subCategory)
      );
    }
    return data;
  }, [products, category, selectedFilters]);

  // ✅ Sort data
  const sortedData = useMemo(() => {
    let data = [...filteredData];
    if (sortBy === "NameAsc")
      data.sort((a, b) =>
        (a.product_name || "").localeCompare(b.product_name || "")
      );
    if (sortBy === "NameDesc")
      data.sort((a, b) =>
        (b.product_name || "").localeCompare(a.product_name || "")
      );
    return data;
  }, [filteredData, sortBy]);

  // ✅ Pagination
  const totalPages = Math.ceil(sortedData.length / itemsPerPage);
  const paginatedData = sortedData.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  // ✅ Toggle subcategory filter
  const toggleSubcategory = (sub) => {
    setSelectedFilters((prev) => {
      const subs = prev.subCategory.includes(sub)
        ? prev.subCategory.filter((s) => s !== sub)
        : [...prev.subCategory, sub];
      return { ...prev, subCategory: subs };
    });
  };

  // ✅ Auto close filter panel
  useEffect(() => {
    if (filterPanelOpen) {
      let timeoutId;
      const startTimer = () => {
        clearTimeout(timeoutId);
        timeoutId = setTimeout(() => setFilterPanelOpen(false), 2000);
      };
      const handleMouseEnter = () => clearTimeout(timeoutId);
      const handleMouseLeave = () => startTimer();
      const panel = filterPanelRef.current;

      if (panel) {
        panel.addEventListener("mouseenter", handleMouseEnter);
        panel.addEventListener("mouseleave", handleMouseLeave);
      }

      startTimer();
      return () => {
        clearTimeout(timeoutId);
        if (panel) {
          panel.removeEventListener("mouseenter", handleMouseEnter);
          panel.removeEventListener("mouseleave", handleMouseLeave);
        }
      };
    }
  }, [filterPanelOpen]);

  return (
    <div className="shop-container">
      {/* Banner */}
      <div className="Banner-container">
        <div className="banner">
          <img src="/images/ShopBanner.jpg" alt="Banner" />
          <div className="overlay-text">
            <h1>{category || "Shop"}</h1>
          </div>
        </div>
      </div>

      {/* Filter Panel */}
      <div
        ref={filterPanelRef}
        className={`filter-panel ${filterPanelOpen ? "open" : ""}`}
      >
        <h3>Filters</h3>
        <button
          className="clear-filters-btn"
          onClick={() => setSelectedFilters({ subCategory: [] })}
        >
          Clear Filters
        </button>

        <div>
          <h4>Subcategories</h4>
          {Array.from(new Set(products.map((d) => d.subCategory))).map((sub) => (
            <label key={sub}>
              <input
                type="checkbox"
                checked={selectedFilters.subCategory.includes(sub)}
                onChange={() => toggleSubcategory(sub)}
              />
              {sub}
            </label>
          ))}
        </div>

        <button onClick={() => setFilterPanelOpen(false)}>Apply Filters</button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Controls */}
        <div className="controls">
          <button onClick={() => setFilterPanelOpen(!filterPanelOpen)}>
            Filters
          </button>
          <div className="sort">
            <label>Sort By: </label>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
            >
              <option value="Featured">Featured</option>
              <option value="PriceAsc">Price Low to High</option>
              <option value="PriceDesc">Price High to Low</option>
              <option value="NameAsc">Name A-Z</option>
              <option value="NameDesc">Name Z-A</option>
            </select>
          </div>
        </div>

        {/* Product Grid */}
        <div className="product-list">
          {paginatedData.length > 0 ? (
            paginatedData.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))
          ) : (
            <p>No products found.</p>
          )}
        </div>

        {/* Pagination */}
        <div className="pagination">
          <span>Total Products: {sortedData.length}</span>
          <div>
            <label>Items per page:</label>
            {[16, 20, 24].map((num) => (
              <button
                key={num}
                onClick={() => {
                  setItemsPerPage(num);
                  setCurrentPage(1);
                }}
              >
                {num}
              </button>
            ))}
          </div>
          <div>
            <label>Page:</label>
            <select
              value={currentPage}
              onChange={(e) => setCurrentPage(Number(e.target.value))}
            >
              {Array.from({ length: totalPages }, (_, i) => (
                <option key={i} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Shop;
