import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ProductDisplay from "./product";

const Item = () => {
  const { id } = useParams();
  const [productId, setProductId] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8000/products/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch product");
        return res.json();
      })
      .then((data) => {
        if (Array.isArray(data) && data.length > 0) {
          setProductId(data[0]);
          console.log("Fetched product:", data[0]);
        } else {
          setProductId(null);
          console.log("Product not found");
        }
      })
      .catch((err) => {
        console.error("Fetch error:", err);
      });
  }, [id]);

  if (!productId) {
    return <div>Loading product...</div>;
  }

  // Parse images
  let images = [];
  const { product_images } = productId;

  if (typeof product_images === "string") {
    try {
      const jsonFixed = product_images.replace(/'/g, '"');
      const parsed = JSON.parse(jsonFixed);
      images = parsed.map((obj) => Object.keys(obj)[0]);
    } catch {
      images = product_images.match(/https?:\/\/[^\s'"]+/g) || [];
    }
  } else if (Array.isArray(product_images)) {
    images = product_images;
  }

  return (
    <div className="product-page">
      <ProductDisplay product={productId} images={images} />
    </div>
  );
};

export default Item;
